INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1113', '12', '36501001113', '有机黄豆（真空）', '+', '97', '45', '', '1000', '0.315', '10.20', '8.50', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2;font-size:16px;\"><strong><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422233807_99147.jpg\" alt=\"\" height=\"249\" width=\"250\" align=\"right\" />产品介绍</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"line-height:2;\">&nbsp;&nbsp;&nbsp; </span><span style=\"line-height:2;\">黄豆又名大豆，因豆皮色黄而得名。北大荒产大豆弛名已久，它兼有粮食、油料二者之长，是一种营养丰富的可口美味食物，由其制做成的豆浆，在质和量上均可以与动物蛋白质相媲美，被誉为“绿色的牛乳”。</span><span style=\"line-height:2;font-size:14px;\">本品采用真空包装，体积小、易携带、无杂质、长期保存不霉变，有效保存了杂粮中的各种营养物质。</span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:16px;\"><strong>食疗效果 </strong></span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">1.提升免疫，大豆含植物性蛋白质，有“植物肉”的美称。人体如果缺少蛋白质，会出现免疫力下降、容易疲劳的症状。吃黄豆补蛋白，可避免吃肉胆固醇升高的问题。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">2.让头脑聪明，黄豆富含大豆卵磷脂，它是大脑的重要组成成分之一。多吃黄豆有助预防老年痴呆症。此外，大豆卵磷脂中的甾醇，可增加神经机能和活力。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">3.健器官，大豆卵磷脂还能促进脂溶性维生素的吸收，强健人体各组织器官。另外，它可以降低胆固醇，改善脂质代谢，预防和治疗冠状动脉硬化。</span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\"></span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\"></span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\"> </span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">4</span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422233416_74144.jpg\" alt=\"\" height=\"308\" width=\"400\" align=\"left\" /></span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">.提高精力，大豆中的蛋白质，可以增加大脑皮层的兴奋和抑制功能，提高学习和工作效率，还有助于缓解沮丧、抑郁的情绪。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">5.美白护肤，黄豆富含大豆异黄酮，这种植物雌激素能改善皮肤衰老，还能缓解更年期综合征。此外，日本研究人员发现，黄豆中含有的亚油酸可以有效阻止皮肤细胞中黑色素的合成。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">6.预防癌症，大豆含有蛋白酶抑制素，美国纽约大学研究员实验发现，它可以抑制多种癌症，对乳腺癌的抑制效果最为明显。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">7.阻止氧化，黄豆中的大豆皂苷能清除体内自由基，具有抗氧化的作用。它还能抑制肿瘤细胞的生长，增强人体免疫机能。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">8.降低血脂，大豆中的植物固醇有降低血液胆固醇的作用。它在肠道内可与胆固醇竞争，减少胆固醇吸收。在降低高脂血症患者血液中的“坏胆固醇”的同时，不影响血液中的“好胆固醇”，有很好的降脂效果。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:12px;line-height:1.5;\">9.预防耳聋，补充铁质可以扩张微血管，软化红血球，保证耳部的血液供应，可以有效防止听力减退。黄豆中铁和锌的含量较其他食物高很多，对预防老年人耳聋有一定作用。 </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:1.5;font-size:12px;\">10.辅助降压，美国科学家研究发现，高血压患者的饮食中，摄入的钠过多，而钾过少。摄入高钾食物，可以促使体内过多的钠盐排出，有辅助降压的效果。黄豆含有丰富的钾元素，每100克黄豆含钾量高达1503毫克。高血压患者常吃黄豆，对及时补充体内钾元素很有帮助。</span>\n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"><strong>食用方法</strong></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 磨豆浆、煮粥、煲汤。</span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 常温下干燥.阴凉处</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1113_P_1334336335544.jpg', 'images/201204/goods_img/1113_P_1334336335544.jpg', 'images/201204/source_img/1113_P_1334336335544.jpg', '1', '', '1', '1', '0', '0', '1334336334', '100', '0', '0', '0', '0', '0', '0', '1338760613', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1112', '12', '36501001112', '有机高粱米（真空）', '+', '82', '45', '', '1000', '0.400', '8.88', '7.40', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"font-size:14px;line-height:2;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422234325_16117.jpg\" alt=\"\" height=\"374\" width=\"300\" align=\"left\" /></span><span style=\"font-size:16px;line-height:2;\"><strong>产品介绍</strong></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;line-height:2;\">高粱米是高粱碾去皮层后的颗粒状成品粮。高粱又称红粮、蜀黍，古称蜀秫。主要产区集中在东北地区、内蒙古东部以及西南地区丘陵山地。按其性质分，有粳性和糯性两种，粒质分为硬质和软质。籽粒色泽有黄色、红色、黑色、白色或灰白色、淡褐色五种。中国的名酒如茅台、五粮液、泸州老窖、汾酒等都以红高粱为主要原料。 </span>\n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"><strong>食疗功效</strong></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp; </span><span style=\"font-size:14px;line-height:2;\">1、 高粱味甘、性温、涩，入脾、胃经；</span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp; </span><span style=\"font-size:14px;line-height:2;\">2、 具有和胃、消积、温中、涩肠胃、止霍乱的功效； </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp; </span><span style=\"font-size:14px;line-height:2;\">3、 主治脾虚湿困、消化不良及湿热下痢、小便不利等症。 </span>\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-size:16px;line-height:2;\"><strong>食用方法</strong></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;line-height:2;\">高粱米是东北城乡人民的主要食粮之一，可以做米饭，也可磨粉和制作各种面食。&nbsp;</span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;line-height:2;\">常温下干燥.阴凉处</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1112_1335488066062.jpg', 'images/201204/goods_img/1112_1335488066062.jpg', 'images/201204/source_img/1112_1335488066062.jpg', '1', '', '1', '1', '0', '0', '1334336334', '100', '0', '0', '0', '0', '0', '0', '1334388085', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1111', '12', '36501001111', '有机黑米（真空）', '+', '77', '45', '', '1000', '0.415', '19.92', '16.60', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"font-size:14px;line-height:2.5;\"></span> \n</p>\n<span style=\"font-size:24px;line-height:2;\"><strong><span> 产 品 介 绍</span></strong></span> <span style=\"font-size:18px;line-height:2;\"><strong></strong></span> \n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong><img height=\"533\" alt=\"\" src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422234908_57093.jpg\" width=\"400\" align=\"left\" /></strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"> </span><span style=\"font-size:16px;line-height:2;\">黑米是一种药、食兼用的大米，属于糯米类。黑米是由禾本科植物稻经长期培育形成的一类特色品种。粒型有籼、粳两种，粒质分糯性和非糯性两类。糙米呈黑色或黑褐色。黑米外表墨黑，营养丰富，有“黑珍珠”和“世界米中之王”的美誉。</span><span style=\"font-size:16px;line-height:2;\">中医认为黑米有显著的药用价值，古农医书记载：黑米“滋阴补肾， 健身暖胃，明目活血”，“清肝润肠”，“滑湿益精，补肺缓筋”等功效；可入药入膳，对头昏目眩、贫血白发、腰膝酸软、夜盲耳鸣症、疗效尤佳。长期食用可延年益寿。因此，人们俗称 ：“药米”、“长寿米”。由于它最适于孕妇、产妇等补血之用，又称“月米”、“补血米”等。历代帝王也把它作为宫廷养生珍品，称为“贡米”。现代医学证实，黑米具有滋阴补肾，健脾暖肝、补益脾胃，益气活血，养肝明目等疗效。经常食用黑米，有利于防治头昏、目眩、贫血、白发、眼疾、腰膝酸软、肺燥咳嗽、大便秘结、小便不利、肾虚水肿、食欲不振、脾胃虚弱等症。由于黑米所含营养成分多聚集在黑色皮层，故不宜精加工，以食用糙米或标准三等米为宜。按照营养专家黎黍匀研究，黑米的生命力指数为7.7021,食物防病指数为36.05，具有较强的保健功能。 </span> \n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>食用方法</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"></span><span style=\"font-size:16px;line-height:2;\">煮粥、煮杂饭 </span><span style=\"font-size:18px;line-height:2;\"><strong><br />\n</strong></span> \n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">避免阳光直射，置阴凉干燥处</span> \n</p>\n<br />\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1587_1334696263564.jpg', 'images/201204/goods_img/1587_1334696263564.jpg', 'images/201204/source_img/1587_1334696263564.jpg', '1', '', '1', '1', '0', '0', '1334336330', '57', '0', '0', '0', '0', '0', '0', '1334737871', '1', '', '-1', '-1', '0', '0', '');
