INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1582', '15', '36501001581', '一级压缩黑木耳', '+', '72', '45', '', '1000', '0.250', '64.32', '53.60', '0.00', '0', '0', '1', '', '', '<p style=\"text-align:left;\">\n	<span style=\"font-size:18px;line-height:2;\"><strong>产品介绍 </strong></span>\n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;黑木耳是著名的山珍，可食、可药、可补，中国老百姓餐桌上久食不厌，有“素中之荤”之美誉，世界上被称之为“中餐中的黑色瑰宝”。而黑木耳培植方法，在世界农艺、园艺、菌艺史上，都堪称一绝。</span> \n</p>\n<p style=\"text-align:center;\">\n	<span style=\"font-size:16px;line-height:2;\"><img height=\"525\" alt=\"\" src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422220240_19684.jpg\" width=\"700\" /> </span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>营养价值</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;黑木耳被营养学家誉为‘素中之荤’和‘素中之王’，每100克黑木耳中含铁185毫克，它比绿叶蔬菜中含铁量最高的菠菜高出20倍，比动物性食品中含铁量最高的猪肝还高出约7倍，是各种荤素食品中含铁量最多的。黑木耳是一种味道鲜美、营养丰富的食用菌，含有丰富的蛋白质、铁、钙、维生素、粗纤维，其中蛋白质含量和肉类相当，铁比肉类高10倍，钙是肉类的20倍，维生素B＿2是蔬菜的10倍以上，黑木耳还含有多种有益氨基酸和微量元素，被称之为 “素中之荤”。100克黑木耳的营养成分为：含水分10.9克、蛋白质10.6克、脂肪0.2克、碳水化合物65.5克、热量306千卡、粗纤维7.0 克、灰分5.8克、钙357毫克、磷201毫克、铁185毫克、胡萝卜素0.03毫克、硫胺素0.15毫克、核黄素0.55毫克、烟碱酸2.7毫克。</span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>食疗效果</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">1、木耳中铁的含量极为丰富，故常吃木耳能养血驻颜，令人肌肤红润，荣光焕发，并可防治缺铁性贫血； </span>\n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">2、木耳含有维生素K，能减少血液凝块，预防血栓症的发生，有防治动脉粥样硬化和冠心病的作用；</span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">3、木耳中的胶质可把残留在人体消化系统内的灰尘、杂质吸附集中起来排出体外，从而起到清胃涤肠的作用。它还有帮助消化纤维类物质功能，对无意中吃下的难以消化的头发、谷壳、木渣、沙子、金属屑等义务有溶解与烊化作用，因此，它是矿山、化工和纺织工人不可缺少的保健食品。</span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">4、它对胆结石、肾结石等内源性异物也有比较显著的化解功能； </span>\n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">5、木耳含有抗肿瘤活性物质，能增强机体免疫力，经常食用可防癌抗癌。</span> \n</p>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<span style=\"font-size:18px;line-height:2;\"><strong><img height=\"504\" alt=\"\" src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422220145_99479.jpg\" width=\"700\" /><br />\n</strong></span>\n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>食用方法</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;将产品泡发洗净，煮汤、热炒或者凉拌均可。</span> \n</p>\n<p>\n	<span style=\"font-size:18px;line-height:2;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;line-height:2;\">常温下干燥.阴凉处</span> \n</p>\n<br />\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201206/thumb_img/1582_1339106324504.jpg', 'images/201206/goods_img/1582_1339106324504.jpg', 'images/201206/source_img/1582_1339106324504.jpg', '1', '', '1', '1', '0', '0', '1334649498', '100', '0', '0', '0', '0', '0', '0', '1334649498', '1', '', '-1', '-1', '0', '0', '');
