INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1118', '12', '36501001118', '有机糙米（真空）', '+', '74', '45', '', '1000', '0.405', '11.52', '9.60', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2;font-size:16px;\"><strong>产品介绍</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp;&nbsp;&nbsp; 糙米，是指除了外壳之外都保留的全谷粒。即含有皮层、糊粉层和胚芽的稻米。日本人则称糙米为玄米。由于口感较粗，质地紧密，煮起来也比较费时；但是糙米的营养价值比精白米高。去壳后仍保留存些许外层组织，如皮层、糊粉层和胚芽。上述的外层组织内含丰富的营养，比起白米更富有许多维他命、矿物质与膳食纤维，所以糙米向来被视为是一种健康食品。糙米也可制成谷片，通常是搭配牛奶一起当早餐食用。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;\"><strong>产品功效</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp; </span><span style=\"line-height:2;font-size:14px;\">1、 糙米有提高人体免疫功能，促进血液循环，消除沮丧烦躁的情绪，降低血糖，预防心血管疾病、贫血症、便秘、肠癌等功效。</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp; </span><span style=\"line-height:2;font-size:14px;\">2、 《本草纲目》中认为，谷芽能消烦、活中、益精、健脾、止泻，对人体十分有益。</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp; </span><span style=\"line-height:2;font-size:14px;\">3、 中医认为糙米味甘、性温，健脾养胃、补中益气，调和五脏、镇静神经、促进消化吸收。 </span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;\"><strong><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120422/20120422230408_58413.jpg\" alt=\"\" align=\"left\" />食用方法</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp; </span><span style=\"line-height:2;font-size:14px;\">1、煮粥、制作糕点、煮杂饭等 </span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp; </span><span style=\"line-height:2;font-size:14px;\">2、糙米茶\n材料：糙米180cc(一碗), 水八合(八碗) \n作法：用没沾油的锅，把糙米翻炒而不要爆裂的炒到黄褐色为止。\n同时在锅中放八碗水煮开后放进炒过的糙米马上停火。\n原封不动放五分钟。\n将糙米过滤后当茶喝。 功效： \n　　糙米茶有清血作用及分解胆固醇，能让血液在血管里流通更畅顺，可预防高血压与脑中风，血液如果畅通，供给体系包的养分和氧气充足自然全身各部器官就会\n健康，免疫能力就会增强而达到预防疾病的效果。 \n　　促进小便畅通，糙米茶是最天然的利尿剂，有促进新陈代谢的作用，排出体内过剩的养分及毒素，更有帮助胰脏分泌胰岛素之功能，能降低血糖，是糖尿病患者\n的最佳食疗饮料。 　　促进大便畅通，糙米因含有最丰富的天然植物纤维，有助於排出肠内宿便，预防大肠、直肠癌及结肠癌。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp;&nbsp;&nbsp; 常温下干燥.阴凉处</span> \n</p>\n<p>\n	<br />\n</p>\n<br />\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1118_P_1334395369360.jpg', 'images/201204/goods_img/1118_P_1334395369360.jpg', 'images/201204/source_img/1118_P_1334395369360.jpg', '1', '', '1', '1', '0', '0', '1334336352', '100', '0', '0', '0', '0', '0', '0', '1334388085', '1', '', '-1', '-1', '0', '0', '');
