INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1128', '23', '36501001128', '羌都红枣', '+', '78', '56', '', '1000', '1.000', '184.80', '154.00', '0.00', '0', '0', '1', '', '', '<p style=\"text-align:center;\">\n	<span style=\"line-height:2.5;font-size:14px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120423/20120423164402_17238.jpg\" alt=\"\" width=\"400\" /></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:16px;\"><strong></strong></span><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14px;\">“羌都红枣”出产自素有“华夏第一县”之称的新疆若羌县境内，地处巴音郭楞蒙古自治州东南部，北纬36°—41°23′， 是世界水果专家公认的“水果优生区带”，四周被沙漠和戈壁包围，沙漠干热高温与阿尔金山冷空气昼夜在此交锋，形成特大日温差，造就了中国精品红枣之乡。\n“羌都红枣” 独处千里茫茫戈壁沙漠之中，甜脆适口，香味浓厚，多食不厌，优质制干红枣含糖量高，色红、皮薄肉厚，紧凑密实，果肉细腻，清甜爽口，堪称枣中极品。\n羌都精品红枣天然挂干，呈长圆形，个头均匀，果形饱满、皱缩度小、易于储藏，表皮光滑洁净、呈暗红色、果面皱纹浅，红枣果肉紧密厚实、有韧性，食具粘性、味香甜、具有本品种独特的回味；维生素含量是苹果的50倍，梨子的100倍，水份含量≤20%。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"font-size:16px;\"><strong>产品特征</strong></span><br />\n<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\"></span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 一、光热地理优势：800米左右最佳海拔，年近210天无霜期，塔克拉玛干大沙漠干热高温与阿尔金山雪域凉爽气流昼夜置换，形成高达27.8摄氏度以上日温差，独有的光热资源和巨大的昼夜温差，成就了若羌红枣异乎寻常的品质。与内地红枣比，若羌红枣不裂果、耐储存、不生虫，干枣含糖量高达68%，是别处红枣无法比拟的。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 二、 不凡品质优势：皮薄而久煮不裂，核小而摇之脆响，肉厚而坚实细腻、味甘且浓香爽口，色红而气韵高贵，形端而圆润饱满。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 三、 神奇生灵优势：在滴水贵如金的沙漠之地，羌都红枣竟然超凡脱俗、生机勃发，这是大自然精气凝结成的神奇精灵。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 四、 养生健体优势：昆仑支脉、阿尔金山山脉泉溪水富含多种人体所需矿物质，润育出羌都红枣强身健体延年益寿之奇效。“常食若羌‘羌都枣’，无须昆仑‘盗仙草’”。&nbsp;&nbsp;</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 五、 绿色时尚优势：若羌绿洲深处千里无人烟的茫茫沙漠戈壁，背靠阿尔金山，宛如世外桃源。羌都红枣在树上自然挂干，无需任何连晒工艺，无病虫害，无须化肥农药，自成绿色食品。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 六、 西域文化优势：吃着子母河畔、女儿国度、罗布老人、楼兰后裔献上的红枣，可尽情领略神秘的丝路风情、回味悠远的楼兰文化。\n   医圣张仲景的《伤寒论》和明朝李时珍的《本草纲目》中均有记载：红枣有润心肺、止咳定喘、补五脏、治虚损、调营卫、缓阴血、生津液、悦颜色、除肠胃邪气之功用；红枣主治腹气、平胃气、通九窍助十二经；补少气、少津液、身中不足；和百药、补血降压、补肾健脑。久服轻身、养颜、益发、延年。可谓“百果之冠”。 具有保肝、健脾、降低胆固醇、升高白血球、抗过敏等作用。 <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\"> <span style=\"background-color:#E53333;color:#FFE500;\">【注意】：糖尿病人最好少食用，含糖量太高。</span> <br />\n</span></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;\"><span style=\"font-size:14px;\"><br />\n</span></span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1128_P_1334336554067.jpg', 'images/201204/goods_img/1128_P_1334336554067.jpg', 'images/201204/source_img/1128_P_1334336554067.jpg', '1', '', '1', '1', '0', '0', '1334336553', '100', '0', '0', '0', '0', '0', '0', '1334388085', '1', '', '-1', '-1', '0', '0', '');
