INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1204', '31', '36501001204', '藏牦牛肉干 麻辣味', '+', '31', '22', '', '1000', '0.250', '47.76', '39.80', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 可可西里牦牛肉干选用生长在海拔3000多米的世界之颠青藏高原食天然冬虫夏草饮雪山泉水的可可西里牦牛肉为原料。牦牛肉富含蛋白质，钙，磷，血红蛋白等多种人体必需的营养素。野牦牛和高原精灵藏羚羊生活在天然草场，草丛中像雪莲、冬虫夏草、贝母等这样罕见的名贵的中药材有上千种，牦牛除觅食野青草外,还吃冬虫夏草、雪莲等天然药用植物,故藏区谚语道:\"牛吃虫草我吃牛,无病无灾药不求\"。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 可可西里牦牛肉干采用牦牛腿鲜肉为原料，经选料、预煮、整形、炒制、烘干、包装等工序精制而成；配方传统，工艺考究，品质鲜纯，具有独特的牦牛肉香味；高蛋白、低脂肪，含有人体所需的多种微量元素和氨基酸，营养丰富；既是人们娱乐或旅游的休闲食品，也是探亲访友的馈赠之佳品和绿色食品。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 可可西里牦牛肉干是利用藏式独有的秘制方法，精心加工而成。口感好，口味多样（有咖喱牦牛肉干，麻辣牦牛肉干，五香牦牛肉干，五香牦牛肉派，酱味牦牛肉派，丁香牦牛肉派，麻辣牦牛肉派，藏式卤汁味等），肉质鲜嫩，是低脂肪，高能量，高蛋白，口味极好的美味佳品，更是老少皆宜的天然绿色营养食品。 藏牦牛，是青藏高原特有的耐寒动物，躯体笨重，粗壮。雄牛一般肩高205厘米，体重1140公斤，雄雌牦牛均有角，主要分布在果洛、玉树、海西、海北等人迹罕至，海拔4000－4500米之间空气稀薄，终年积雪的高山崇岭、山间盆地、高寒荒漠等恶劣严酷的环境中，也适于伏卧冰雪上。在气温较高的季节，又常活动于雪线下缘。牦牛在青海境内分布广泛，它和高原精灵藏羚羊生活在一起，到处都是自然草场，草丛中像雪莲、冬虫夏草、贝母等这样罕见的名贵的中药材有上千种，由于高山环绕，地形封闭，地处边陲，草场的生长环境是无任何抗生素及其它化学品污染，是难得的天然牧场，牦牛除觅食野青草外,还吃冬虫夏草、雪莲等天然药用植物,故藏区谚语道:\"牛吃虫草我吃牛</span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1204_P_1334396492486.jpg', 'images/201204/goods_img/1204_P_1334396492486.jpg', 'images/201204/source_img/1204_P_1334396492486.jpg', '1', '', '0', '1', '0', '0', '1334336786', '100', '0', '0', '0', '0', '0', '0', '1336523917', '1', '', '-1', '-1', '0', '0', '');
