INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1207', '31', '36501001207', '老闫家南瓜子', '+', '41', '23', '', '1000', '0.238', '21.60', '18.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> 陕北人历来喜用南瓜子招待贵客，亮堂堂的窑洞，暖暖的炕，炒瓜子的婆姨和满窑洞飘着炒南瓜籽的清香，成为家乡客人美好、温馨的记忆。\n南瓜子富含锌、铁、不饱和脂肪酸、蛋白质、胡萝卜素和多种维生素，是一种美味与食补兼具的好食品。\n老闫家精品南瓜子选用上等老南瓜籽为原料，经过筛选、清洗和手工分拣等工序，用陕北传统工艺炒制而成，是您居家休闲、招待贵客、馈赠亲友之放心食品。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>南瓜子五大宝</strong> ：</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; 1、锌---是人体上百种酶的活化剂,是前列腺必须的营养物质,它可激发人体潜能。 <br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; 2 、氨基酸---独特的南瓜子氨酸可驱虫排毒,尤其适用驱绦虫 蛲虫 蛔虫等寄生虫。 <br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; 3 、不饱和脂肪酸---南瓜子中的不饱和脂肪酸(油酸、亚油酸)占总脂肪酸的76.9%-91.5%,不饱和脂肪酸是防治心脑血管疾病的营养物质,可以降血糖,缓解高血压。 <br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; 4 、类脂---类脂有很强的抗氧化性,可延缓人体衰老。 <br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; 5、微量元素---微量元素含量丰富,可补充人体所需。 </span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1207_P_1334336787220.jpg', 'images/201