INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1265', '31', '36501001265', '全新绿色小镇豆腐竹', '+', '33', '119', '', '1000', '0.250', '48.00', '40.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">豆腐竹是一種黃豆製品，以高溫製程，並利用多道繁雜的手續，精製而成；外表不起眼的豆腐竹，卻有著高蛋白質的營養，比一般豆皮的營養價值更高，並且久煮不爛、QQ口感的特點，還去除了豆皮油炸的油膩感，清爽可口，不會造成身體多餘的負擔，多多食用好處。\n豆腐竹也可加入味噌湯或蘿蔔湯中煮。海帶結的湯可當湯底。\n綠色小鎮嚴選商品，無添加防腐劑、無化學添加劑，好吃不加料！\n豆腐竹DIY： <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; ★豆腐竹魯大白菜做法：\n材料: 豆腐竹、大白菜、木耳、油豆腐、金針花、海帶結。\n調味料: 醬油、八角滷包。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 做法: <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 1. 將海帶結泡水15分鐘；豆腐竹洗淨泡水15分鐘泡軟備用。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 2.鍋中留油4大匙放入薑片及香菇爆香再放入大白菜。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 3.以大火將大白菜炒至半熟最後放入泡好的豆腐竹及調味料，所有食材一起放入鍋中。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 4.煮滾後倒入砂鍋中續用小火燜煮20分鐘至軟透即可。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; ★豆腐竹湯做法：\n材料：豆腐竹一包、江魚仔半碗、蒜3瓣、蛋一只、油適量、水半鍋\n調味：鹽兩小匙 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">做法：</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n1. 豆腐竹泡軟瀝乾待用。<br />\n&nbsp;&nbsp;&nbsp; 2. 把江魚仔洗淨瀝乾，蒜拍扁切碎。開鍋熱油將江魚仔和蒜米爆香，然後連油撈起放進燒開的半鍋水中，熬成上湯。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 3. 當湯色呈乳白色之後，加入泡軟的豆腐竹轉小火煮至腐竹軟透。然後，加入適量的鹽調味要上桌之前，別忘了在湯裡頭打個漂亮的蛋花。 </span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1265_P_1334336991973.jpg', 'images/201204/goods_img/1265_P_1334336991973.jpg', 'images/201204/source_img/1265_P_1334336991973.jpg', '1', '', '0', '1', '0', '0', '1334336990', '100', '0', '0', '0', '0', '0', '0', '1336523859', '1', '', '-1', '-1', '0', '0', '');
