INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1287', '31', '36501001287', '蜜柚果脯', '+', '44', '100', '', '1000', '0.150', '18.00', '15.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 欧尼柚制作商南海集团有限公司已有二十五年历史，拥有万亩水果生产基地，2万吨气调保鲜库，琯溪蜜柚出口量在全国处于领先地位。公司拥有年产6000吨果脯、软糖，3000吨果浆、果茶，10万吨蜜柚浓缩汁、20万吨蜜柚饮料，以及符合GMP规范的国际一流的自动化生产线。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>关于蜜柚：</strong> <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 柚子品种繁多，有西柚，金丝柚，蜜柚，沙田柚等品种\n沙田柚皮其实不太薄，皮的外观不是特别光滑的那种，果型也是梨型的居多。沙田柚一般就一两斤多，不超过三四斤；蜜柚随便就是三四斤一个的，还又大又圆，而且感觉外皮很光滑蜜柚的汁水非常丰富，比沙田柚丰富一些；西柚比较小，比橙子大点，里面是粉红色的，一般比较酸也叫葡萄柚；金丝柚因产量低，一种相信已被人遗忘的特产，果仅拳头大小，皮薄、无渣、无核、汁多、肉爽、酸甜适中。\n琯溪蜜柚至今有近500年的栽培历史，并久负盛名。清乾隆年间，琯溪蜜柚被列为朝廷贡品，同治皇帝爱极而独占，曾赐“西圃信记”印章一枚和青龙旗一面，作为标志和禁令，在琯溪蜜柚未成熟时就印识其上，民间又称“皇榜柚”。施鸿葆在所著《闽杂记》一书中称琯溪蜜柚为福建三大名果之一:“荔枝为美人，福桔为名士，若平和抛，则侠客也，香味绝胜”。可见，琯溪蜜柚在清朝就享有盛誉。浙江农业大学一级教授吴耕民先生评价琯溪蜜柚：“果大皮薄、瓤肉无籽、色白如玉、多汗柔软、入口深化、不留残渣、清甜微酸、味极永隽，可列为柚类之冠”。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>&nbsp;功效与作用： </strong><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 中医认为，柚子味甘、酸，性寒，有健胃化食、下气消痰、轻身悦色等功用。现代医药学研究发现，柚肉中含有非常丰富的维生素C以及类胰岛素等成分，故有降血糖、降血脂、减肥、美肤养容等功效。经常食用，对高血压、糖尿病、血管硬化等疾病有辅助治疗作用，对肥胖者有健体养颜功能。\n柚子还具有健胃、润肺、补血、清肠、利便等功效，可促进伤口愈合，对败血症等有良好的辅助疗效。此外，柚子含有生理活性物质皮甙，可降低血液的黏滞度，减少血栓的形成，故而对脑血管疾病，如脑血栓、中风等也有较好的预防作用。而鲜柚肉由于含有类似胰岛素的成分，更是糖尿病患者的理想食品。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>营养价值：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;\n柚子含有糖类、维生素B1、维生素B2、维生素C、维生素P、胡萝卜素、钾、磷、枸橼酸等。柚皮主要成分有柚皮甙、新橙皮甙等，柚核含有脂肪油、黄柏酮、黄柏内酯等。柚子营养丰富，每100克可食部分，含水分84.8克、蛋白质0.7克、脂肪0.6克、碳水化合物12.2克、热量57千卡、粗纤维0.8克、钙41毫克、磷43毫克、铁0.9毫克、胡萝卜素0.12毫克、硫酸素0.07毫克、核黄素0.02毫克、尼克酸0.5毫克、抗坏血酸41毫克。\n中医认为，柚子味甘、酸，性寒，有健胃化食、下气消痰、轻身悦色等功用。现代医药学研究发现，柚肉中含有非常丰富的维生素C以及类胰岛素等成分，故有降血糖、降血脂、减肥、美肤养容等功效。经常食用，对高血压、糖尿病、血管硬化等疾病有辅助治疗作用，对肥胖者有健体养颜功能。\n柚子还具有健胃、润肺、补血、清肠、利便等功效，可促进伤口愈合，对败血症等有良好的辅助疗效。此外，柚子含有生理活性物质皮甙，可降低血液的黏滞度，减少血栓的形成，故而对脑血管疾病，如脑血栓、中风等也有较好的预防作用。而鲜柚肉由于含有类似胰岛素的成分，更是糖尿病患者的理想食品。</span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1287_P_1334337062208.jpg', 'images/201204/goods_img/1287_P_1334337062208.jpg', 'images/201204/source_img/1287_P_1334337062208.jpg', '1', '', '0', '1', '0', '0', '1334337061', '100', '0', '0', '0', '0', '0', '0', '1336523838', '1', '', '-1', '-1', '0', '0', '');
