INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1291', '31', '36501001291', '阿胶糕(平阴玫瑰)', '+', '49', '119', '', '1000', '0.245', '105.60', '88.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> 济南紫金玫瑰有限公司坐落于山东省济南市平阴济西工业园区，玫瑰湖湿地南邻，这里风景秀丽，气色宜人。平阴素有“中国玫瑰之乡”和“中国阿胶之乡”之美称，公司本着“发掘地域特色资源，打造绿色地域产品，造福人类健康”为宗旨，先后成功研制开发了玫瑰和阿胶两大系列产品，主要品种有：玫瑰酱、玫瑰膏、玫瑰花糖糕、玫瑰花蕾、玫瑰精油、速食阿胶、即食阿胶糕、阿胶膏、阿胶枣等。现注册商13个，拥有多项国家发明专利，并通过了玫瑰食品和阿胶食品的QS认证。公司产品新颖，质量过硬，深受广大消费者的喜爱和信赖。</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong> 商品简介：</strong><br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> 本品精选地道东阿产阿胶为主要原料，辅以黑芝麻、核桃仁、红枣、桂圆、冰糖、黄酒，结合民间传统服用阿胶之方法，采用现代工艺技术精制而成。\n本品胶香纯正，味香甜可口，即开即食，服用方便。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><br />\n</span> \n</p>\n<p>\n	<span style=\"font-size:16px;line-height:2;\"><strong>营养价值：</strong></span><br _extended=\"true\" />\n<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 核桃生食营养损失最少，在收获季节不经干燥取得的鲜核桃仁更是美味。目前，吃鲜核桃仁在发达国家比较普遍。有关科技开发中心正在攻克鲜核桃仁的加工保鲜技术，相信在不久的将来，全国的消费者就能随时随地吃到营养美味的鲜核桃仁了。关于核桃仁的食用量，一般认为每天吃5～6个核桃，约20～30克核桃仁为宜；吃得过多，会生痰、恶心。此外，阴虚火旺者、大便溏泄者、吐血者、出鼻血者应少食或禁食核桃仁。</span><br _extended=\"true\" />\n</p>', 'images/201204/thumb_img/1291_P_1334337085890.jpg', 'images/201204/goods_img/1291_P_1334337085890.jpg', 'images/201204/source_img/1291_P_1334337085890.jpg', '1', '', '0', '1', '0', '0', '1334337084', '100', '0', '0', '0', '0', '0', '0', '1336523836', '1', '', '-1', '-1', '0', '0', '');
