INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1295', '31', '36501001295', '椒盐巴旦木', '+', '42', '7', '', '1000', '0.235', '33.60', '28.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 巴旦杏营养价值很高，它的营养比同重量的牛肉高六倍。据化验，仁内含植物油５５－６１％，蛋白蛋２８％，淀粉、糖１０－１１％，并含有少量维生素Ａ、Ｂ１、Ｂ２和消化酶、杏仁素酶、杏仁成、钙、镁、钠、钾，同时含有铁、钴等１８种微量元素。巴旦杏仁是维吾尔人传统的健身滋补品，有人每日睡觉前细嚼十余粒，开水冲下，长期食用，夜间能通宵熟睡无梦，身体抵抗力显著增强，保持身强体壮。\n天然程度\n每逢初秋，当果园里的巴旦杏由绿变黄微带红晕，开始千裂成熟的时节，人们就络绎不绝地前去选购采摘，迅速将成熟的巴旦杏争购一空。\n除了盐椒巴旦木是用佐料炒过的以外，其他种类的巴旦木都纯天然的。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>养身意义</strong> ：</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 1、健脑开窍安神：儿童食用可促进大脑发育，老年人食用即可安神，又可健脑，可用于老年性脑萎缩，老年性痴呆症的食疗品，同时也可作为一般的健身食品用。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 2、健肾作用：将巴旦杏制成酱食用，取得了良好的效果，可用于肾虚患者的辅助治疗，因为其口感好，无毒无副作用，乐于被患者接受，所以可长期服用。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 3、明目作用：维吾尔族人近视眼发病率明显较汉族人低，与本民族一些好的生活及饮食习惯有关。巴旦杏是维吾尔人经常食用的一种食品，从某一方面讲，在长期的食用中，巴旦杏发挥了它的明目作用。\n二、比较\n原味巴旦木\n原味巴旦木味道比较纯正，带一点木香口味，可以入药，仁味超过核桃和杏仁，有特殊的甜香风味。\n盐椒巴旦木\n盐椒巴丹木较硬,像一个杏核,但咬开里面的干果很甜很好吃,口感脆。和大杏仁的味道有点象，吃起来很香。口味比较浓重，咸咸的，比较适合青少年。\n纸皮巴旦木\n皮都是自然开口的，手一剥就开，比盐椒的味道要淡，比薄皮的味道要浓。\n薄皮巴旦木\n皮比纸皮的厚一点，但是没有纸皮的薄，味道像大杏仁。\n三、口感\n口感香脆、味道甘甜、富有甜香风味 </span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1295_P_1334337098230.jpg', 'images/201204/goods_img/1295_P_1334337098230.jpg', 'images/201204/source_img/1295_P_1334337098230.jpg', '1', '', '0', '1', '0', '0', '1334337097', '100', '0', '0', '0', '0', '0', '0', '1336523833', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1296', '31', '36501001296', '野核桃', '+', '46', '7', '', '1000', '0.285', '62.40', '52.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"></span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong><span style=\"font-size:14px;line-height:2;\">产品介绍：</span></strong>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 野核桃，别名山核桃、小核桃、秋核桃、胡桃秋。分布范围：野核桃(J.cathayensis Dode)分布于新疆、江苏、江西、浙江、四川、贵州、甘肃、陕西,以及东北,华北大部，垂直分布在海拔800～2000米。 野核桃自然保护区位于新疆维吾尔自治区西北部伊犁河谷地巩留县。</span>\n</p>\n<p>\n	<br />\n<span style=\"font-size:14px;line-height:2;\"></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>营养价值：</strong><br />\n&nbsp;&nbsp;&nbsp;&nbsp; 1．核桃仁含有较多的蛋白质及人体营养必需的不饱和脂肪酸，这些成分皆为大脑组织细胞代谢的重要物质，能滋养脑细胞，增强脑功能； </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 2．核桃仁有防止动脉硬化，降低胆固醇的作用；此外，核桃还可用于治疗非胰岛素依赖型糖尿病；核桃对癌症患者还有镇痛，提升白细胞及保护肝脏等作用； </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 3．核桃仁含有的大量维生素E，经常食用有润肌肤、乌须发的作用，可以令皮肤滋润光滑，富于弹性； </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 4．当感到疲劳时，嚼些核桃仁，有缓解疲劳和压力的作用。 </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 山核桃核仁松脆味甘，香气逼人，可榨油、炒食，也可作为制糖果及糕点的佐料。山核桃果仁中含有7.8-9.6%的蛋白质，人体必需的氨基酸占7种以上且含量高达25%以上，还含有22种人体所需的微量元素，其中钙、镁、磷、锌、铁含量十分丰富。据资料分析，每公斤核桃仁等于9公斤鲜牛奶或5公斤鸡蛋、2公斤牛肉的营养价值，而且它较牛奶、鸡蛋、牛肉的营养易为人体吸收。核仁还具有润肺补气、养血平喘、润燥化痰去虚寒诸功效。 </span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 除了种仁，山核桃的外果皮和根皮果可供药用，鲜根皮煎汤浸洗，治脚痔(脚趾缝湿痒)，鲜外果皮捣取汁擦治皮肤癣症。<br />\n</span>\n</p>', 'images/201204/thumb_img/1296_P_1334337100587.jpg', 'images/201204/goods_img/1296_P_1334337100587.jpg', 'images/201204/source_img/1296_P_1334337100587.jpg', '1', '', '0', '1', '0', '0', '1334337100', '100', '0', '0', '0', '0', '0', '0', '1336523832', '1', '', '-1', '-1', '0', '0', '');
