INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1301', '31', '36501001301', '野生天麻', '+', '43', '11', '', '1000', '0.125', '123.60', '103.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 为兰科植物。其植物为一株肉质独苗，黄红色。它没有兰草的绿叶，没有牡丹的丽花，没有玫瑰的芳香，但在她的根部却长着稀有珍贵的天麻。  波密野生天麻\n天麻的神奇在于它的周围始终有云雾缭绕。天麻靠密环菌供给养料，密环菌适宜在潮湿的土壤中繁殖，而潮湿的土壤由于水的蒸发而在其周围形成雾罩。天麻为稀为名贵的药用植物，其块茎是平肝息风的良药。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 据化验，天麻中含有“香英兰醇”和维生素A，它对肝风引起的头痛有特效。除此之外，还可以益气、祛风湿、强筋骨。主治高血压、头痛眩晕、口眼歪斜、肢体麻木、小儿惊厥等症。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> 块茎天麻呈长椭圆形，略扁，皱缩而弯曲。一般两端钝圆，上端有时带有枯干残茎，下端有凹陷呈圆盘状的根痕,外表呈黄白色、黄棕色或黄褐色，全身多纵沟，有油点状的须根痕组成横 向环纹。质坚而紧密，不易折断。横切面为牙白色或棕黄色，为半透明体，味略甜带辛中药材。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 野生天麻是一种珍贵的中药材。洞口县山区的原始林木枝繁叶茂，气候温和，夏季较凉爽、冬季又不十分严寒，适合天麻生长。 </span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1301_P_1334337120248.jpg', 'images/201204/goods_img/1301_P_1334337120248.jpg', 'images/201204/source_img/1301_P_1334337120248.jpg', '1', '', '0', '1', '0', '0', '1334337119', '100', '0', '0', '0', '0', '0', '0', '1336523823', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1302', '31', '36501001302', '桂圆肉', '+', '41', '11', '', '1000', '0.250', '57.60', '48.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 桂圆肉是无患子科植物龙眼的假种皮。</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 1、温补：小孩常感冒体质虚冷常尿床、记忆力不佳，多喝桂圆茶可增进脑力，改善虚冷体质。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 2、丰胸：中医医说女人主血气，常吃桂园的女人，脸色红润、身材丰满，所以很多丰胸的补方，都以桂圆作为搭配。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 3、安眠：中医说桂圆具有安神养心、补血益脾的功效，非常适合长期失眠者食用。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 4、补血：能改善心血管循环、安定精神状况、舒解压力和紧张，桂圆含有丰富葡萄糖、蔗糖、蛋白质及多种维生素和微量元素，有良好的滋养补益作用。可用于治疗病后体弱或脑力衰退，妇女在产后调补也很适宜。 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp; <strong>适宜人群</strong> </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 上班族：工作压力大、容易失眠或记忆减退。  桂圆肉\n学生：常熬夜、读书精神耗损，多吃桂圆肉可增强记忆力，避免脑力衰退有很好的功效。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 减肥的人：怕减肥减错地方（可能胸部变小）白天多吃桂圆肉，达到丰胸的目的。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 失眠的人：有安神补血，充实脑力的功效。 　　妇女经期及产后：有补血功能。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp; 脑力衰退、体弱：安神补血，快速恢复体力。</span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1302_P_1334337123799.jpg', 'images/201204/goods_img/1302_P_1334337123799.jpg', 'images/201204/source_img/1302_P_1334337123799.jpg', '1', '', '0', '1', '0', '0', '1334337122', '100', '0', '0', '0', '0', '0', '0', '1336523822', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1303', '31', '36501001303', '小米鲊', '+', '53', '42', '', '1000', '0.400', '20.16', '16.80', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>产品介绍：</strong></span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 小米是贵州人最喜欢的小吃之一。传说，苗王携女寻游山寨，至山民喳幺家中，喳幺家徒四壁，无以款待，就将小米拌以山枣一，放在火塘里蒸熟，取名“小米喳”。苗王怒其不恭，然其女见小米喳色泽灿烂，欣然食之，觉得甘香可口，笑语嫣然。苗王见状食之，亦香亦糯，很是美味，转怒为喜，令喳幺回寨专做小米喳。逢各寨主来朝，必以小米喳待之。苗疆各寨重大节庆，皆以苗王所赐小米喳为上品，以示贵重</span>。<br />\n<br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp; &nbsp; &nbsp; “苗姑娘”牌系列产品采用传统生产工艺，结合现代科学配方，精心酿制而成。现已形成三大系列二十多个品种，产品被贵阳市评为“旅游特色产品”，2006年被评为贵阳市农业产业化重点龙头企业。“苗姑娘”系列辣椒调味品、休闲食品，具有民族风情，产品特色深受广大消费者喜爱。产品已作为西南航空、南方航空、重庆航空公司的配餐食品，公司是一个具有严谨科学管理制度的发展企业。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随着新世纪信息时代的到来,面对西部大开发的机遇和挑战,为提高“苗姑娘”产品质量及适应市场竞争，站稳脚跟,不断发展壮大公司实力,企业已通过ISO9001—2000质量管理体系认证，和绿色食品和HACCP论证。</span>\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"></span><br />\n<span style=\"font-size:14px;line-height:2;\"><strong>配料：</strong>糯小米、白砂糖、大豆油、食盐</span><br />\n<br />\n<span style=\"font-size:14px;line-height:2;\"></span><span style=\"font-size:14px;line-height:2;\"><strong>食用方法：</strong></span><br />\n<br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1、连袋用碗蒸10-15分钟；</span><br />\n<br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2、连内袋煮10-15分钟；</span><br />\n<br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3、碗装在微波炉加热5分钟以上</span><br />\n<br />\n</p>', 'images/201204/thumb_img/1303_P_1334337127317.jpg', 'images/201204/goods_img/1303_P_1334337127317.jpg', 'images/201204/source_img/1303_P_1334337127317.jpg', '1', '', '0', '1', '0', '0', '1334337126', '100', '0', '0', '0', '0', '0', '0', '1336523820', '1', '', '-1', '-1', '0', '0', '');
