INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1320', '23', '36501001320', '刀郎四星（原枣）', '+', '75', '101', '', '1000', '0.500', '81.60', '68.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"></span> \n</p>\n<p>\n	<br />\n</p>\n<span style=\"font-size:14px;\">原产地：新疆</span><br />\n<span style=\"font-size:14px;\"> 果大、皮薄、肉厚、核小、味甘它就是极受人们喜爱的新疆刀郎枣。每一颗“刀郎原枣”都要经过精种、精培、精采、精选等苛刻环节，保障了“刀郎原枣”的卓越品质。目前已通过“有机转换”认证。直接食用、煲汤、煮粥均可，营养健康，单粒包装更易于携带哦</span><br />\n<br />\n<br />\n<img src=\"http://img01.ttmimg.com/60/D5/1331783288185.jpg\" /><img src=\"http://img01.ttmimg.com/5C/00/1331783307411.jpg\" /><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">红枣菊花粥：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：红枣50g，大米90g，菊花15g</span><br />\n<span style=\"font-size:12px;\"> 制作：三者加水适量，煮熟至稠食用</span><br />\n<span style=\"font-size:12px;\"> 功效:可使面部肤色红润，起到防病保健、驻颜美容的作用</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">百合红枣粥：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：江米300克，百合9克，红枣10枚，白糖适量</span><br />\n<span style=\"font-size:12px;\"> 制法：</span><br />\n<span style=\"font-size:12px;\"> （1）先将百合用水泡一下，以去除一部分苦味</span><br />\n<span style=\"font-size:12px;\"> （2）江米淘净，和百合、红枣用文火缓熬成粥，加白糖适量即成</span><br />\n<span style=\"font-size:12px;\"> 功效：对植物神经紊乱、更年期综合症、有清虚火、安心神、治失眠之疗效</span><br />\n<span style=\"font-size:12px;\"> 按语：这一种粥，特别适合女性朋友。江米甘平，能益气止汗；百合甘苦微寒，能清热安神，治虚火、利二便函。辅以红枣甘温，养心补血安神</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">红枣粳米粥：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：红枣50克，粳米100克</span><br />\n<span style=\"font-size:12px;\"> 制作：同煮成粥</span><br />\n<span style=\"font-size:12px;\"> 功效：此粥性平和，养血安神，补虚健脾胃</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">红枣汤：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：红枣15枚</span><br />\n<span style=\"font-size:12px;\"> 制作：将红枣洗净，加水2碗，浓煎成一碗</span><br />\n<span style=\"font-size:12px;\"> 服法：吃枣喝汤，早晚空腹食用。连服7日左右</span><br />\n<span style=\"font-size:12px;\"> 功效：补脾胃、益气血。适用于脾虚便溏、胃虚食少、气血不足，以及过敏性紫颠、血小板减少等症</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">绞股蓝红枣汤：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：绞股蓝10克，红枣5枚</span><br />\n<span style=\"font-size:12px;\"> 制作：两物洗净，同放锅内，加水适量，文火煮至红枣熟</span><br />\n<span style=\"font-size:12px;\"> 用法：每日1剂，吃红枣，喝汤</span><br />\n<span style=\"font-size:12px;\"> 功效：健脑益智，镇静安神。适宜于神疲乏力，食欲不振，失眠健忘，夜尿频多者服用</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">红枣香菇汤：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：干香菇20只，红枣８枚，料酒、精盐、味精、姜片、花生油各适量</span><br />\n<span style=\"font-size:12px;\"> 制作：将干香菇先用温水浸发至软，再用凉清水洗去泥沙；将红枣洗净，去核。用有盖炖盅，加进澄清过滤的泡发香菇的水和加入适量清水，再放入香菇、红枣、精盐、味精、料酒、姜片、熟花生油少许，盖上盅盖，上蒸笼蒸１小时左右</span><br />\n<span style=\"font-size:12px;\"> 用法：出笼即可食用</span><br />\n<span style=\"font-size:12px;\"> 功效：滑润适口，甜、咸、香、辣味均有，但口味柔和鲜香</span><br />\n<span style=\"font-size:12px;\"> 按语：香菇含有较高的蛋白质、糖、维生素、尼克酸、微量元素和多种氨基酸、酶、核酸等。其性味甘平，有健胃益气、滋补强壮的作用。核酸类物质，可抑制血清\n和肝脏中的胆固醇增加，有阻止血管硬化和降血压作用，可防病健身抗衰老。香菇中含有麦角固醇，经人体吸收后可转化为维生素Ｄ，因而可防治佝偻病和贫血，对\n 健美的有重要意义</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">红枣鸡蛋汤：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：鸡蛋两个，红枣60克，红糖、水适量</span><br />\n<span style=\"font-size:12px;\"> 制作：红枣泡软，去核，加水500毫升煮沸30分钟，再将鸡蛋轻轻打入汤中，勿搅拌，煮熟后加入红糖即成</span><br />\n<span style=\"font-size:12px;\"> 用法：常服食</span><br />\n<span style=\"font-size:12px;\"> 功效：一般体质虚弱、早衰、面色萎黄不华者也可用之补养</span><br />\n<span style=\"font-size:12px;\"> 按语：这是民间广泛用于产后补养气血的食疗方。其搭配合理，营养丰富。久服此汤，可使气血旺盛，颜面肌肤丰润光泽，故有美容之效</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">花生衣红枣汤:</span><br />\n</strong><span style=\"font-size:12px;\"> 原料:花生米100克，干红枣50克，红糖适量</span><br />\n<span style=\"font-size:12px;\"> 制作:花生米用温水泡半小时，取皮；干红枣洗净后温水泡发，与花生米皮同放铝锅2、内，倒入泡花生米水，加清水适量，小火煎半小时，捞出花生衣，加适量红糖即成</span><br />\n<span style=\"font-size:12px;\"> 用法:每日3次，饮汁吃枣</span><br />\n<span style=\"font-size:12px;\"> 功效:养血补血。适用于身体虚弱、产后、病后血虚以及贫血等症</span><br />\n</span><br />\n<span style=\"font-size:x-small;\"><strong><span style=\"font-size:12px;\">丰胸黑木耳红枣汤：</span><br />\n</strong><span style=\"font-size:12px;\"> 原料：黑木耳10克，红枣50克，白糖适量</span><br />\n<span style=\"font-size:12px;\"> 制作：加适量的水，把黑木耳和红枣煮熟后，加入白糖即可</span><br />\n<span style=\"font-size:12px;\"> 用法：月经前１个礼拜到月经结束这段时间饮用，每天吃或隔天食用都可以</span><br />\n<span style=\"font-size:12px;\"> 功效：此汤适合血虚及脾虚型的人饮用，特别是合并有月经量多的女性最为适合，此汤方有健脾、补血、调经的功效，对月经量较多的女性，有调节的作用，促使脾胃功能正常,血气丰沛，乳房自然就会发育完整</span></span>', 'images/201204/thumb_img/1320_P_1334396838572.jpg', 'images/201204/goods_img/1320_P_1334396838572.jpg', 'images/201204/source_img/1320_P_1334396838572.jpg', '1', '', '1', '1', '0', '0', '1334337187', '100', '0', '0', '0', '0', '0', '0', '1334388083', '1', '', '-1', '-1', '0', '0', '');
