INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1406', '38', '36501001406', '云南红茶 和系列圆听', '+', '89', '105', '', '1000', '0.100', '117.60', '98.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424140301_16166.png\" alt=\"\" /><br />\n</span> \n</p>\n<p>\n	<br />\n</p>\n<span style=\"line-height:2;\"> 中茶”牌红茶，源自红茶特定产区优质茶园，经过严格的品质控制与精湛的发酵工艺精制而成。汤色红艳明亮，金圈突出，香气蜜甜浓郁，滋味浓厚鲜醇，是红茶之佳品。和系列云南红茶是中茶世界茶园系列中的高规格产品，自饮、送礼均属上佳。\n中茶和系列内涵:至醇思和，心旷神怡与人合着，谓之人乐。与天河者，谓之天乐。用心饮茶者，虚怀若谷。心思天下，大和。唯有好茶，才真正令人身心释然，豁然开朗。\n红茶属于发酵茶类，是以茶树的芽叶为原料,经过萎凋、揉捻（切）、发酵、干燥等典型工艺过程精制而成。因其干茶色泽和冲泡的茶汤以红色为主调，故名红茶。我国红茶种类较多，产地较广，主要有祁门红茶、滇红工夫茶、政和工夫茶、坦洋工夫茶、白琳工夫茶等。\n世界四大红茶为：祁门红茶（Keemun 中国）、大吉岭红茶（Darjeeling 印度）、锡兰红茶（Uva 斯里兰卡）、阿萨姆红茶（Assam 印度）。\n滇红工夫茶和滇红碎茶主销俄罗斯、波兰等东欧各国和西欧、北美等30多个国家和地区。内销全国各大城市。滇红的品饮多以加糖加奶调和饮用为主，加奶后的香气滋味依然浓烈。冲泡后的滇红茶汤红艳明亮，高档滇红，茶汤与茶杯接触处常显金圈，冷却后立即出现乳凝状的冷后浑现象，冷后浑早出现者是质优的表现。滇红工夫茶最大的特征为茸毫显露，毫色分淡黄、菊黄、金黄。凤庆、云昌等地生产的滇红工夫茶，毫色呈菊黄；临沧、勐海等地所产，毫色则多为金黄。即使是在同一茶园，不同季节的茶色也各不相同，春茶毫色较浅，多呈淡黄，夏茶毫色多呈菊黄，秋茶则多为金黄色。滇红工夫茶的另一大特征为香郁味浓。香气以滇西云县、凤庆、昌宁为佳，尤以云县部分茶区所出为最，这里所产滇红工夫的香气中带有花香。滇南所产之茶。滋味浓厚，刺激性较强，但回味不及滇西之工夫茶。滇红工夫茶中，品质最优的是\"滇红特级礼茶\"，以1芽1叶为主制造而成，成品茶条索紧直肥壮，苗锋秀丽完整，金毫多而显露，色泽乌黑油润，汤色红浓透明，滋味浓厚鲜爽，香气高醇持久，叶底红匀明亮。最适宜作高级礼品。 </span> \n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1608_1334697003627.jpg', 'images/201204/goods_img/1608_1334697003627.jpg', 'images/201204/source_img/1608_1334697003627.jpg', '1', '', '1', '1', '0', '0', '1334337542', '3', '0', '0', '0', '0', '0', '0', '1335489489', '1', '', '-1', '-1', '0', '0', '');
