INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1431', '38', '36501001431', '日本玉露茶（进口茶） 粹系列方听', '+', '62', '105', '', '1000', '0.051', '309.60', '258.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120423/20120423143306_17871.png\" alt=\"\" /><br />\n</span>\n</p>\n<p>\n	<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; <span style=\"line-height:2;\">日本玉露保留着自我国唐代流传过去的“蒸青”绿茶制作技艺，并通过不断改进成为日本茶中的高级精品，素有“日本国茶”的美誉。玉露茶树栽培方法独特，茶树开始发芽时，茶农就会搭起稻草扎成的蓬，阻挡阳光直射树顶，而遮荫的特殊光合作用，会让鲜叶色泽更加鲜绿和厚重，氨基酸等的鲜美成分会大幅度提升。下雨时，雨水会将稻草特有的香气渗透进茶叶里。 采摘时，茶农采用传统的手摘方法将茶叶采下，迅速送到工厂，约蒸30秒以保持风味和阻止发酵，然后用热空气使茶叶变软，挤压干燥直至含水量降到原来的30%。重复揉捻，使茶叶变成纤细暗绿的针状，然后挑拣出茶柄和老叶，再干燥。 玉露茶香清幽，甘甜柔和，茶汤翠绿，气味上更青绿，有着不食人间烟火的仙气。</span><span style=\"line-height:2;\">使用铁壶煮水可以软化水质，用这样的水冲泡玉露茶可以提升口感。除了正常的冲泡方法，玉露还可以冰饮，即用冰块代替水，在玉露上面放上适量的冰块，让冰块慢慢自然融化后，就可以品到独一无二的“冰出玉露”的鲜美味道。 像红酒一样，玉露绿茶在成熟陈化的过程中更加醇厚、甘醇，据说可以保存十年之久。新茶和窖藏数年的茶，味道截然不同。玉露最好放在0至10度温度中保存，木制冷库保存效果更好。</span></span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1431_P_1334397040528.jpg', 'images/201204/goods_img/1431_P_1334397040528.jpg', 'images/201204/source_img/1431_P_1334397040528.jpg', '1', '', '1', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334388083', '1', '', '-1', '-1', '0', '0', '');
