INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1439', '38', '36501001439', '天尖 简装', '+', '65', '105', '', '1000', '0.125', '42.00', '35.00', '0.00', '0', '0', '1', '', '', '<br />\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> </span>\n</p>\n<h1 class=\"title\" id=\"lemmaTitle-2927548\">\n	天尖\n</h1>\n<div class=\"album-view layoutleft log-set-param layoutParagraphBefore layoutParagraphAfter\" data-layout=\"left\" id=\"alb_335391\" style=\"width:219px;\">\n	<p class=\"album-div\" style=\"background-color:#fff;height:162px;width:219px;\">\n		<span class=\"album-border1\" style=\"height:145px;width:202px;\"></span><span class=\"album-border2\" style=\"height:145px;width:202px;\"></span><a class=\"album-overlay  nslog:1212\" href=\"http://baike.baidu.com/albums/2927548/2927548/1/335391.html#335391\" target=\" _blank\"=\"\" style=\"height:162px;width:219px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120423/20120423141035_55619.png\" alt=\"\" /><br />\n<span class=\"album-logo\"></span></a> \n	</p>\n<span class=\"title\"></span><span class=\"count\"></span> \n</div>\n<p>\n	<a target=\"_blank\" href=\"http://baike.baidu.com/view/2255732.htm\"><span style=\"font-size:16px;line-height:2;\"><br />\n</span></a>\n</p>\n<p>\n	<a target=\"_blank\" href=\"http://baike.baidu.com/view/2255732.htm\"><span style=\"font-size:16px;line-height:2;\"><br />\n</span></a>\n</p>\n<p>\n	<a target=\"_blank\" href=\"http://baike.baidu.com/view/2255732.htm\"><span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp; 安化黑茶</span></a><span style=\"font-size:16px;line-height:2;\">的制作原料均为黑毛茶，黑毛茶又按等级分为“芽尖、白毛尖、天尖、贡尖、乡尖、生尖、捆尖”七类，其中以“芽尖”为极品，但因数量极少，未能成为市场交易的商品，故在时下市场流通的黑茶产品中，以天尖茶为最佳。 天尖茶古时为茶商馈送达官、富商、亲友的高贵礼品，清道光年间（1825年前后）被列为贡品，\n专供皇室饮用，现成为南方有钱人的一种饮用时尚，该茶既可以泡饮，也可煮饮；既适合清饮亦适合制作奶茶，特别在南方各茶馆煮泡壶茶，家庭煎泡冷饮茶，很合\n时宜。现在全国许多省、市饮用天尖茶已成为一种潮流，特别是南方的广州、西北的西安。 天尖茶采用谷雨时节的鲜叶加工而成，色泽乌黑、口感甘爽、独具纯正松烟香、滋味醇厚、汤色深黄明亮、叶底匀齐尚嫩、且品质稳定，长期饮用具有减肥、降脂、降血、提高机体免疫力等功效。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1439_1335488308698.jpg', 'images/201204/goods_img/1439_1335488308698.jpg', 'images/201204/source_img/1439_1335488308698.jpg', '1', '', '1', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334388083', '1', '', '-1', '-1', '0', '0', '');
