INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1470', '26', '36501001470', '刺五加晶', '+', '78', '80', '', '1000', '0.800', '230.40', '192.00', '0.00', '0', '0', '1', '', '', '<p>\n	<img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424001834_87040.jpg\" alt=\"\" width=\"739\" />\n</p>\n<p>\n	<img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424001849_20752.jpg\" alt=\"\" width=\"739\" />\n</p>\n<p>\n	<img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424001931_41849.jpg\" alt=\"\" width=\"739\" /><br />\n<span><strong><span style=\"color:#FF6600;font-family:幼圆;font-size:16px;line-height:1.5;\"><span style=\"font-size:16px;\"> </span></span></strong></span>\n</p>\n<p>\n	<span><strong><span style=\"color:#FF6600;font-family:幼圆;font-size:16px;line-height:1.5;\"><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 现代研究证实，刺五加含有多种五加甙、多糖等活性成分，具有\n抗衰老、抗疲劳（刺五加甙的抗疲劳作用比人参还强呢）、抗癌，和调节神经、内分泌、心血管功能，提高机体对物理、化学、生物等有害剌激的抵抗能力等药理作\n用。在神经衰弱、失眠、高血压病、冠心病、脑梗塞、脑动脉硬化、更年期综合征、血脂异常、糖尿病、风湿病、慢性支气管炎以及肿瘤等疾患的治疗中，都有一定\n疗效。而且，刺五加可以提高人体摄氧量，能提高人体组织利用氧气的能力，使活动机群得到更多氧，并有效延缓疲劳发生。</span></span> </strong></span>\n</p>\n<p>\n	<strong><br />\n</strong> \n</p>\n<p>\n	<strong><span style=\"color:#FF6600;font-family:幼圆;\"><span style=\"font-size:16px;line-height:1.5;\">刺五加的效用：</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">一、增加氧吸收量：已有研究证实，服用刺五加可以增加人体的氧气吸收量，并且提高细胞氧气交换的能力。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">A：在人体方面：人体有足够的氧气来进行体内燃烧，并促进新陈代谢，是可以使肌肤常保弹性与美丽。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">B：运动方面：可以提升最大耗氧量、增加最大换气量、增加运动持续时间。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">C：抗氧化作用：刺五加的抗氧化效果可是维他命Ｅ的五倍喔！有良好的抗氧化作用。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">D：减少疲劳感：帮助人体增加氧气的吸收量，减低疲劳。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">二、增加抵抗力：透过配醣体机能的功效，进而增加抵抗力。(抗SARS有效)</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">三、调整体质：透过消化道机能的维持，进而调整体质。(抗过敏效果好)</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">四、调节生理机能：对于病后者具有良好的调节功能，可为病后之补养，故有益于调节生理机能。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">五、滋补强身：可促进食欲、增强体力。(抗流行性感冒、气喘有效)</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">六、延年益寿：富含丰富矿物质元素，降血脂、降血压。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\"> 适用对象：</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">上班族工作压力、应酬多、加班熬夜，需要白天精神应对各种问题，晚间轻松入睡；</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">学生族应对繁重学业压力、各种考试；</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">病人康复期及体质虚弱者滋养身体、产后恢复；</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">运动员体力耗损；</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">老年人调养生息，保持身体活力、平时滋补。</span><br />\n<span style=\"font-size:16px;line-height:1.5;\">这款刺五加绿茶呢，是选用完达山脉野生中草药刺五加嫩叶，并且与优质的葛根、枸杞、绿茶等为原料、采用先进生产工艺精制加工而成。此款绿茶还经过中国茶叶学会权威鉴定，“汤色黄绿明亮，滋味爽口回甘，具有独特持久清香”。</span></span> </strong> \n</p>\n<br />', 'images/201204/thumb_img/1470_P_1334337991742.jpg', 'images/201204/goods_img/1470_P_1334337991742.jpg', 'images/201204/source_img/1470_P_1334337991742.jpg', '1', '', '1', '1', '0', '0', '1334337991', '5', '0', '0', '0', '0', '0', '0', '1335362689', '1', '', '-1', '-1', '0', '0', '');
