INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1478', '25', '36501001478', '蜂胶软胶囊', '+', '50', '80', '', '1000', '0.050', '182.40', '152.00', '0.00', '0', '0', '1', '', '', '<p>\n	<br />\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"line-height:2;font-size:16px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424005244_13666.jpg\" alt=\"\" width=\"730\" /><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color:#E56600;\">蜂胶是蜜蜂从植物幼芽树干上采集的树脂、树液等，并混入其上颚腺分</span></span><span style=\"line-height:2;font-size:16px;color:#E56600;\">泌物等混合而成的具有芳香气味的胶状固体物，呈黄褐色、暗绿色、味</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">苦、辛辣味。蜂胶的成分极其复杂，目前人类已知的有多种黄酮类化合</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">物、酸类化合物多种芳香酸多种脂肪酸及多糖类物质、酚类以及铁、</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">锰、钴、硒等多种微量元素，蜜蜂将这些物质涂布在整个蜂巢的表面；</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">一方面起防腐、抗氧化作用一方面来粘合蜂巢，堵塞缝隙等，使众多病</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">菌无法在蜂巢内滋生。</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;color:#E56600;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">蜂胶素有\"紫色黄金\"之称。更为奇特的是,蜂胶在对症下药的同时,</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">其它各种成份非但不会对人体构成危害,反而能起到全面调理,平衡机体</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">免疫功能的作用,这也是许多消费者在服用蜂胶过程中常常出现意外惊</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">喜的原因所在。</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-size:16px;color:#E56600;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">在蜂胶内发现的化学物质的种类有：蜂蜡、树脂类、香脂类、</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">芳香油、脂溶性油类、花粉和其它有机物。研究表明，蜂胶中的树脂类</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">物质的来源有三种：蜜蜂所采集的植物分泌液、蜜蜂体内代谢的分泌物</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">和蜂胶形成过程中介入的物质。</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">新采集的蜂胶约含有55％树脂和香膏，30％蜂蜡，10％芳香挥发</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">油，5％的花粉等杂物。 经现代科学（70年代）分析，从蜂胶中分离出</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">多种化学成分，证明蜂胶成分极为复杂。其中有黄酮类化合物、酸、</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">醇、酚、醛、酯、醚以及烯、萜类化合物和多种氨基酸、脂肪酸、酶</span><span style=\"line-height:2;font-size:16px;color:#E56600;\">类、维生素、微量元素等。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1478_P_1334337996889.jpg', 'images/201204/goods_img/1478_P_1334337996889.jpg', 'images/201204/source_img/1478_P_1334337996889.jpg', '1', '', '1', '1', '0', '0', '1334337996', '100', '0', '0', '0', '0', '0', '0', '1334388083', '1', '', '-1', '-1', '0', '0', '');
