INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1492', '25', '36501001492', '蕨麻王', '+', '66', '8', '', '1000', '0.200', '46.56', '38.80', '0.00', '0', '0', '1', '', '', '<p>\n	<br />\n</p>\n<hr />\n<p>\n	<p>\n		<span style=\"font-size:14px;line-height:2;\"><strong>产品介绍：</strong></span>\n	</p>\n	<p>\n		<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 蕨</span><span style=\"font-size:14px;line-height:2;\">麻，为蔷薇科植物鹅绒委陵菜\n(Potentillaanserina)的块根，又名人参果，藏语名戳玛。产于我国东北、华北和西北各地。西藏全境内均有分布，牧区最多，并遍布于青海\n省海拔3000多米的草原上。藏族人称蕨麻为“卓老沙僧”，味甘美，含有大量淀粉、蛋白质、脂肪、维生素，长期食用能延年益寿。蕨麻含有很多生物活性物\n质，如蕨麻多糖、鞣质、蕨麻素和野鸦椿酸等。蕨麻根中尚含胆碱、甜菜碱、组胺酸、琥珀酸、黄酮类化合物等。蕨麻含有粗蛋白10.08%、脂肪1.14%、\n膳食纤维15.18%、灰分3.52%、还原糖1.90%、总糖12.47%、淀粉18.02%。氨基酸种类齐全，不但含有多种氨基酸(17 \n种)，而且含有人体所需的8 种必需氨基酸，其中精氨酸的含量尤为突出，达到16.6%，这是任何其他食物所达不到的。每100 g \n蕨麻中无机元素Ca、P、Mg、Fe、Zn 的含量分别为229.06 mg、370.07 mg、141.13 mg、21.23 mg、20.49 \nmg，都较一般根茎类食品高出很多倍。维生素C 含量特别高，每100 g 中可达19.2 mg。由此可见，蕨麻的营养价值很高。</span>\n	</p>\n</p>\n<span style=\"font-size:14px;line-height:2;\"><strong>公司介绍：</strong></span>\n<p>\n	<p>\n		<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 北京精物堂食品有限公司是大红鲤特产礼品行旗下公司，大红鲤特产礼品行主营全国各地和海外特产，是全国最大、最全的特产礼品店，在销售过程中，发现有些产品很受消费者欢迎，回头客众多，受此启发，决定和国内乃至海外厂商合作开发口碑良好的产品，特成立北京市精物堂食品有限公司，产品涵盖台湾、新疆、宁夏、甘肃、浙江等几乎国内特色产品。因产品为经典，精华的物产，故名“精物堂”。</span><br />\n	</p>\n<span style=\"font-size:14px;line-height:2;\"><strong>食用方法</strong></span>\n</p>\n<span style=\"font-size:14px;line-height:2;\"></span>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 用温水洗净，煮稀饭或做八宝粥食用，或单独煮熟食用</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>营养价值</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 蕨麻含有很多生物活性物质，如蕨麻多糖、鞣质、蕨麻素和野鸦椿酸等。蕨麻根中尚含胆碱、甜菜碱、组胺酸、琥珀酸、黄酮类化合物等</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">置阴凉干燥处,谨防受潮</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>食疗效果</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">益气补血、健胃补脾、生津止渴</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1492_P_1334338389189.jpg', 'images/201204/goods_img/1492_P_1334338389189.jpg', 'images/201204/source_img/1492_P_1334338389189.jpg', '1', '', '1', '1', '0', '0', '1334338389', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1493', '25', '36501001493', '桂花川贝应子', '+', '66', '119', '', '1000', '0.300', '9.36', '7.80', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:24px;\"></span><strong></strong><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424111324_51821.jpg\" alt=\"\" width=\"700\" /><span style=\"font-size:16px;\"><strong><br />\n</strong></span></span>\n</p>\n<p>\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"line-height:2;font-size:14px;\">川贝母为百合科植物川贝母、暗紫贝母、梭砂贝母或甘肃贝母等的鳞茎。川贝母为多年生草本，生于海拔1800至2400米的林中、灌丛下、草地、河滩、山谷等湿地或岩缝中，分布于四川、云南、西藏等地；暗紫贝母为多年生草本，生于海拔3200至4500米的草地上，分布于四川、青海等地；梭砂贝母为多年生草本，生于海拔3000至4700米的砂石地，分布于青海、四川、云南、西藏等地；甘肃贝母为多年生草本，生于海拔2800至4400米的灌木丛中或草地上，分布于甘肃、青海、四川等地。采收季节因各地气候不同而异。野生者多在夏、秋季或积雪融化后杂草未长时采挖。用种子播种栽培的于第三年茎叶枯萎后采挖。用鳞茎及分割鳞茎繁殖的于次年采挖，清除泥土，晒干；阴雨天可烘干，烘温40℃至50℃为宜。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\"><strong>食用方法</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp;&nbsp;&nbsp; 吞食或研磨于梨蒸服</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\"><strong>保存注意事项</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp;&nbsp;&nbsp; 常温下保存，置于通风干燥处</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\"><strong>食疗效果</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-size:14px;\">&nbsp;&nbsp;&nbsp; 能清热化痰,润肺止咳,散结消肿</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1493_P_1334338393645.jpg', 'images/201204/goods_img/1493_P_1334338393645.jpg', 'images/201204/source_img/1493_P_1334338393645.jpg', '1', '', '1', '1', '0', '0', '1334338392', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1494', '25', '36501001494', '鹿茸片', '+', '54', '22', '', '1000', '0.010', '71.76', '59.80', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\">\n	<p>\n		<strong>产品介绍：</strong>\n	</p>\n	<p>\n		<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 鹿茸主产于青藏高原祁连山一带。鹿茸是没有骨化的公鹿角，刚出生时又软又嫩，角外面包着一层皮，皮上长着容貌，里面是软骨，不慢了毛细血管。鹿茸的生长期一般从4月份到7月份，每天可长2-4毫米，80天内就可成茸。本品精心筛选，以上等鹿茸切片而成，鹿茸片是名贵药材，含有多种人体所需要的微量元素，入药与食用都赢得了广大消费者的极高认可，是滋补健身、馈赠亲友的上乘佳品。</span>\n	</p>\n<span style=\"font-size:x-small;\"> \n	<p>\n		<span style=\"line-height:2.5;font-size:14px;\"><strong>产品展示：</strong></span>\n	</p>\n</span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\">\n	<p>\n		<span style=\"line-height:2.5;font-size:14px;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424110557_90004.jpg\" alt=\"\" width=\"700\" /></span>\n	</p>\n</span></span><br />\n<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\"></span></span><span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\">\n	<p>\n		<br />\n	</p>\n	<p>\n		<br />\n	</p>\n	<p align=\"center\">\n		<img alt=\"可可西里鹿茸片包装\" src=\"http://www.skang.cn/images//20111129/d98da824c3ce2a45.jpg\" />\n	</p>\n	<p align=\"left\">\n		<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">产品功效与作用：</span></strong></span></span></span>\n	</p>\n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 壮肾阳：</span></strong><span style=\"font-size:14px;line-height:2;\">就是为人体的生殖系统提供充足的源动力。肾阳为一身阳气之根，主人体生殖系统发育，脾胃的运化、肺气的宣降、三焦水道的通调等。如肾阳不足，生殖系统紊乱，就会出现阳痿，生殖器萎缩，不育症等情况；生殖系统能量储备不足，就会出现畏寒怕冷，腰膝酸软，气短乏力，勃起无力，时间短，硬度差，无精，少精，死精的情况；蒸腾气化功能失常，水液代谢障碍，或气化不利，尿少、水肿，或肾不纳气，小便清长、夜尿增多、尿频、尿急。</span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 益精血：</span></strong><span style=\"font-size:14px;line-height:2;\">就是为人体的生命活动提供充足的物质能量保证。精血，就是肾精与气血的合称，是维持人体生命活动的基本物质。血本源于先天之精，而生成于后天饮食水谷；精的形成，亦靠后天饮食所化生，故有“精血同源”之说，精血的盈亏决定人体的健康与否。如果人体精血不足就会出现，头晕目眩，耳鸣健忘，腰酸腿软，面色苍白，神疲嗜睡，畏寒肢冷，夜尿频多，阳痿早泄，经少、经闭等症状。</span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 强筋骨：</span></strong><span style=\"font-size:14px;line-height:2;\">就是消除人体骨骼的退行性病变。中医论肾主骨生髓，能够加强骨质的合成与生长。通过滋补肾脏，强壮筋骨，对中老年人出现的骨质疏松、关节炎、颈腰椎病都有非常明显的作用。</span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 调冲任：</span></strong><span style=\"font-size:14px;line-height:2;\">就是指调节人体内分泌。冲为太冲脉，任为任脉．着两条脉都属于经脉的奇经八脉。任脉有调理气血，养护男性睾丸和阴茎和女性子宫和卵巢作用；冲脉为”十二经脉之海”与女子排卵及男性生精功能息息相关。任冲失调就是指它们的功能出现障碍。</span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 托疮毒：</span></strong><span style=\"font-size:14px;line-height:2;\">就是指排除人体内部蓄积的大量毒素，提高人体免疫力。</span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">食用方法</span></strong></span></span></span></span></span></span></span></span></span></span></span>：\n	</p>\n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">枸杞鹿茸酒</span></strong><br />\n<span style=\"font-size:x-small;\"><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 材料：枸杞100-150克，鹿茸片40g，50度以上的白酒1升（2斤）</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：将枸杞鹿茸置白酒中中，浸泡二周。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 用法：每日服25-50ml，2次/日饮酒服用。</span></span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<strong><span style=\"font-size:14px;line-height:2;\">枸杞鹿茸茶</span><br />\n</strong><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 材料：枸杞3-5克，鹿茸片2-3克。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：将枸杞鹿茸置杯中，加沸水，盖后5分钟饮用，反复冲服至无味</span>\n	</p>\n	<p align=\"left\">\n		<strong><span style=\"font-size:14px;line-height:2;\">红枣鹿茸茶</span></strong><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 材料：鹿茸片2-3克，红枣5个。 </span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：红枣鹿茸置杯中，加沸水，盖后5分钟饮用，反复冲服至无味。</span>\n	</p>\n	<p align=\"left\">\n		<strong><span style=\"font-size:14px;line-height:2;\">直饮鹿茸茶</span></strong><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 材料：鹿茸片2-3克 </span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：将鹿茸片（或研末）置杯中，加沸水，盖后5分钟饮用，反复冲服至无味。</span>\n	</p>\n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">鹿茸小米大枣粥</span><br />\n</strong><span style=\"font-size:x-small;\"><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 配料：鹿茸2-5克，小米（或粳米）100克 大枣10个。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：用鹿茸片（粉）与小米（或粳米）大枣洗净放入锅内，熬制成粥食用。 </span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 吃法：每日1次，早晨食用。</span></span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">鹿茸老鸡汤</span></strong><br />\n<span style=\"font-size:x-small;\"><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 材料：鹿茸片15克，大枣15个，200克鸡（鸭、鹅、鸽、猪瘦肉等）。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp; &nbsp;&nbsp; 其它作料（如葱、姜、盐）</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 做法：取茸片15克与鸡（鸭、鹅、鸽、猪瘦肉等）、枣或其它佐料置在电饭煲或沙锅内、炖3-5小时后食用，可分多次食之。</span></span></span>\n	</p>\n<span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"> \n	<p align=\"left\">\n		<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><span style=\"font-size:x-small;\"><strong><span style=\"font-size:14px;line-height:2;\">品牌介绍：</span></strong></span></span></span></span></span></span></span></span></span></span></span></span></span></span></span></span></span>\n	</p>\n	<p align=\"left\">\n		<span style=\"font-size:x-small;\"><span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 可可西里企业建立初期就导入CI品牌战略，狠抓产品质量关，已经全面导入了ISO国际质量管理管理体系，并先后通过了ISO9001：2000国际质量体系认证和ISO22000：2005食品安全管理体系认证，同时获得了QS全国食品工业生产许可证认证。</span><br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 可可西里的天然食材全部是西藏本地食材，可可西里看准青藏高原独特的地理环境和生态环境使本品具有天然野生、纯净无污染的特性，食材未加任何添加剂，纯野生、纯天然请广大朋友们放心购买，另外还有“可可西里”的精美包装，把此食材放在家里和赠送亲朋好友，都是你最好的选择。</span></span>\n	</p>\n</span></span></span></span></span></span></span></span></span></span></span></span></span></span></span></span></span><br />\n</span>\n</p>\n<span style=\"line-height:2.5;font-size:14px;\"><span style=\"font-size:14px;line-height:2;\"></span><br />\n</span>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1494_P_1334338396829.jpg', 'images/201204/goods_img/1494_P_1334338396829.jpg', 'images/201204/source_img/1494_P_1334338396829.jpg', '1', '', '1', '1', '0', '0', '1334338395', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
