INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1503', '25', '36501001503', '菊花八宝', '+', '66', '22', '', '1000', '0.500', '33.60', '28.00', '0.00', '0', '0', '1', '', '', '<p style=\"text-align:center;\">\n	<img alt=\"\" src=\"http://img.365chi.net:8080/images/upload/Image/image/20120423/20120423173240_57120.jpg\" width=\"500\" /> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>功效</strong>：八宝菊花为菊科多年生草本植物，是中国传统的常用中药材之一，主要以头状花序供药用。据古籍记载，菊花味甘苦，性微寒；有散风清热、清肝明目和解毒消炎等作用.对口干、火旺、目涩，或由风、寒、湿引起的肢体疼痛、麻木的疾病均有一定的疗效。主治感冒风热，头痛病等。 对眩晕、头痛、耳鸣有防治作用。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>作用</strong>：菊花茶:有明目清肝的作用,有些人就干脆菊花加上枸杞一起泡来喝,或是用蜂蜜菊花茶都对疏肝解郁很有帮助。决名子茶:决名子有清热、明目、补脑髓、镇肝气、益筋骨的作用,若有便秘的人还可以在晚餐饭后饮用,对于治疗便秘很有效果。 </span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>注意事项</strong>：请置于室内阴凉干燥处，避免儿童自行拿取。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>适宜搭配</strong>：薄荷、欧时兰、玫瑰花、茉莉花、金盏花等。 </span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>冲泡方式</strong>：桂花3克、绿茶5克、沸水冲泡。</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1503_P_1334338431458.jpg', 'images/201204/goods_img/1503_P_1334338431458.jpg', 'images/201204/source_img/1503_P_1334338431458.jpg', '1', '', '1', '1', '0', '0', '1334338430', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1504', '25', '36501001504', '蚂蚁王', '+', '59', '22', '', '1000', '0.050', '66.00', '55.00', '0.00', '0', '0', '1', '', '', '<p>\n	<br />\n</p>\n<p>\n	<br />\n<span style=\"font-size:14px;\"><strong>营养成分：</strong></span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;<span style=\"line-height:2;\">&nbsp;&nbsp; </span><span style=\"line-height:2;\">野生青藏蚂蚁体中含有苏氨酸、赖氨酸等27种氨基酸，其中包括人体必须的8种主要氨基酸，蛋白质、钙、B1、B2、B12、E、锌、铁等多种微量元素，尤其是微量元素的含量及丰富比自然界任何食品高10-20倍，并含有柠檬、蚁醛、胸蚁醛柠檬醛等特殊类化合物；还含有ATP、多种酶和铺酸、生物碱、组织胺等生物活性物质；此外还有特殊的“蚂蚁抗生素”即后胸侧极腺素等特种抗菌素。</span></span><br />\n<br />\n<span style=\"font-size:14px;\"><strong>主要功能：</strong></span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 1、骨关节疾病：类风湿、风湿性关节炎、肩周炎、腰腿疼。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 2、心脑血管疾病：冠心病、心肌梗死、脑血栓后遗症、心绞痛、心肌炎。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 3、呼吸系统疾病：气管炎、哮喘、肺结核。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 4、泌尿系统疾病：肾炎、阳痿、早泄、性冷淡、前列腺炎。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 5、消化系统疾病：肝炎、肝硬化、肝腹水、胃病、消化不良、便秘、胆囊炎。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 6、神经系统疾病：头痛、失眠、植物神经功能紊乱。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 7、身体免疫性疾病：红斑狼疮、硬质病、皮肌炎、结节性动脉炎。</span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 8、其他疾病：糖尿病、中晚期癌症、癌症术后、少白头、牙齿脱落。</span><br />\n<br />\n<span style=\"font-size:14px;\"><strong>服用方法及服用量：</strong></span><br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 蚂蚁干用50℃白酒浸泡一周后饮用。饮量随意或研成粉；蚂蚁粉每日三次，每次5克（一小汤匙）。温开水送服，或加入蜂蜜炼丸服用，或装入胶囊服用。 </span><br />\n<b><span style=\"color:red;font-family:宋体;font-size:22pt;\"><br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<br />\n<span style=\"font-size:14px;\">&nbsp;</span><br />\n</span></b>\n</p>\n<p style=\"margin:0cm 0cm 0pt;text-align:left;text-indent:28pt;\" align=\"left\">\n	<span style=\"color:black;font-family:宋体;font-size:14pt;\"></span> \n</p>\n<span style=\"line-height:2.5;font-size:14px;\"></span> \n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1504_P_1334338434595.jpg', 'images/201204/goods_img/1504_P_1334338434595.jpg', 'images/201204/source_img/1504_P_1334338434595.jpg', '1', '', '1', '1', '0', '0', '1334338434', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
