INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1541', '25', '36501001541', '平阴玫瑰酱', '+', '93', '119', '', '1000', '0.200', '32.16', '26.80', '0.00', '0', '0', '1', '', '', '<div style=\"text-align:center;\">\n	<br />\n</div>\n<p>\n	<br />\n<span style=\"font-size:16px;line-height:1.5;\"><strong>产品介绍</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp; 一、是采用纯天然优质的玫瑰鲜花瓣，优质食糖、天然蜂蜜为主要原料，采用传统工艺，现代科技精制而成的纯天然绿色食品。</span><br />\n&nbsp;&nbsp; <span style=\"font-size:14px;line-height:2;\">二、 作为腌制玫瑰酱的鲜花取自于基地内定点地块、定点用户的玫瑰花，花发育充分，瓣厚、色浓、洁净，鲜花进入厂后，平铺在阴凉的通风的室内，经常翻动，防止发热变质，经过1—2天的放置，玫瑰花的花托与花瓣容易分离，剥除花托、花萼后，花瓣的重量约为花重的65％，把混杂在花瓣中的碎叶等杂质挑净后，即可进行腌制。</span><br />\n&nbsp;&nbsp; <span style=\"font-size:16px;line-height:1.5;\"><span style=\"font-size:14px;line-height:2;\">三</span><span style=\"font-size:14px;line-height:2;\">、</span></span><span style=\"font-size:14px;line-height:2;\"> 腌制玫瑰酱的用优质的天然蜂蜜。充分拌匀揉搓，经过充分揉搓的花瓣分层装入瓷缸，压紧。装缸不能装满，以免在其发酵时溢出缸外，之后，放入露天中发酵，为使发酵均匀，开始隔4－5天要翻缸一次，经过2－3次的翻缸，以后就把缸严实地盖好，经过几个月就可食用。</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp; 四</span><span style=\"font-size:14px;line-height:2;\">、玫瑰酱不含任何的防腐剂和添加剂（因为玫瑰本身就有防腐功效）,口感香而美，花香醇厚，香甜可口，风味独特，具有玫瑰保健成份，用后唇齿余香。</span><br />\n&nbsp;&nbsp; <span style=\"font-size:14px;line-height:2;\">五、玫瑰花酱属温性食品，有疏肝醒脾之功能。颇受人们喜爱，是很早就广泛应用于各种糕点、馅和菜肴中的主要原料。</span> \n</p>\n<p>\n	<img alt=\"\" src=\"http://img.365chi.net:8080/images/upload/Image/image/20120423/20120423133523_68691.jpg\" align=\"left\" height=\"334\" width=\"500\" /><br />\n<br />\n<span style=\"font-size:14px;line-height:2;\">【主要成分 】 纯天然优质的玫瑰鲜花瓣，优质食糖、天然蜂蜜。含玫瑰精油、天然vc,va,ve、氨基酸、还原糖及磷、铁等矿物质。</span><br />\n<span style=\"font-size:14px;line-height:2;\">【功 </span><span style=\"font-size:14px;line-height:2;\">效】富营养、润肌肤、养容颜、抗衰老。常期使用保持皮肤水份，香体。具有理气解郁、和血散瘀。主治肝胃气痛，新久风痹，吐血咯血，月经不调，赤白带下，痢疾、乳痈，肿毒。《食物本草》谓其“主利肺脾、益肝胆，食之芳香甘美，令人神爽。”</span><br />\n<span style=\"font-size:14px;line-height:2;\">玫瑰酱可以化解腹部淤血，疼痛，增强表皮细胞活动，促进皮肤新陈代谢，除皱、美白，缓解敏感肤质，是营养皮肤的佳品，常饮可滋润肌肤，使皮肤洁白细腻，保持自然红润。有利于血管的健康，促进血液循环，调理气血、舒缓情绪，由内到外改变人体香源，长期食用能让人的体内散发出香味，大大减少人的体臭、口臭和运动后的汗臭味。本品既能活血散滞，又能解毒消肿，因而能消除因内分泌功能紊乱而引起的面部暗疮等症。长期服用美容效果甚佳，能有效地清除自由基，消除色素沉着，舒肝理气、滋阴养血、红润肌肤和防治月经不调、赤白带下，有美容养颜的作用。</span><br />\n<span style=\"font-size:14px;line-height:2;\">【友情提示 】糖尿病人慎用</span> \n</p>', 'images/201204/thumb_img/1541_P_1334338633662.jpg', 'images/201204/goods_img/1541_P_1334338633662.jpg', 'images/201204/source_img/1541_P_1334338633662.jpg', '1', '', '1', '1', '0', '0', '1334338632', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
