INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1564', '21', '36501001564', '纯椰子粉（无蔗糖）', '+', '95', '119', '', '1000', '0.320', '39.84', '33.20', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"font-size:14px;line-height:2;\"> 海南水好人寿长”是享誉全国的一句话，纯净无污染的水源得益于海南特有的生态自然环境。海南的椰子树是幸福的，优越的成长条件带来的自然是优质的椰子。南国纯椰子粉采用日平均光照量远超椰树正常成长所需光照量的健康椰子，经由新鲜果肉鲜榨的椰浆，喷干后制得。百分之百纯天然绿色健康食品。您在饮用时，已不仅仅是品位椰子的醇香，于午后小憩，呡一小口，舒心怡然间顿觉南国的阳光南国的水如清泉一般润入心田。南国食品奉承健康饮食的人文理念，将海南纯正地道的椰子进行独特工序加工，在保留其浓郁醇香风味的基础上最大限度保留椰子所具有的营养价值，让您和家人在品尝南国椰风海韵的同时更拥有一份健康与美丽！包装上采用易于回收利用的罐装，便于您综合利用，节约生活成本！纯椰子粉——淡然而快乐的生活 <br />\n</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424111405_18399.jpg\" alt=\"\" /><br />\n</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2.5;\"></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"> 本产品选用海南新鲜椰子为主要原料，采用新鲜椰子精制而成。含人体所需的多种氨基酸、维生素和矿物质。该品营养丰富、椰香浓郁、纯正可口、即冲即饮，是老少皆宜的健康饮品。冷天热饮，热天冰冻冷饮,鲜爽宜人。在饮用咖啡、麦片、奶茶时适宜加入本品，味道更独特。</span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120424/20120424111513_40805.jpg\" alt=\"\" /><br />\n<span style=\"font-size:14px;line-height:2;\"> 纯椰子粉不含任何添加剂及糖份，是老年人的上等美味饮品，也是烹调食品时充当调味品的最佳选择。冲调比较：比例为2：1</span><br />\n<span style=\"font-size:14px;line-height:2;\"><strong> 纯椰子粉：</strong>椰浆粉，蛋白质》4%</span><br />\n<span style=\"font-size:14px;line-height:2;\"><strong> 卖点特点：</strong>不含香精、色素、防腐剂；</span><br />\n<span style=\"font-size:14px;line-height:2;\"> <strong>简易卖点：</strong>用椰子原浆制作，无蔗糖。具有天然的椰子甜香口感，而且还可以做烹调。</span><br />\n<span style=\"font-size:14px;line-height:2;\"><strong> </strong><strong>消费对象：</strong>适合只能摄取少量糖分的人群（因为椰子本身含有糖分）。</span><br />\n<span style=\"font-size:14px;line-height:2.5;\"></span> \n</p>', 'images/201204/thumb_img/1564_P_1334338691541.jpg', 'images/201204/goods_img/1564_P_1334338691541.jpg', 'images/201204/source_img/1564_P_1334338691541.jpg', '1', '', '1', '1', '0', '0', '1334338690', '100', '0', '0', '0', '0', '0', '0', '1334388082', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1565', '21', '36501001565', '蜜柚果茶（木糖醇）', '+', '84', '100', '', '1000', '0.375', '30.00', '25.00', '0.00', '0', '0', '1', '福建', '', '<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>蜜柚营养成分</strong></span><br />\n<span style=\"font-size:large;\"></span><br />\n&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:14px;line-height:2;\"> 琯溪蜜柚营养丰富。现代食品分析专家对琯溪蜜柚进行科学研究后发现，每100毫克的果汁含全糖9.17-9.86毫克，可滴定酸0.734-1.011毫克，维生素C48.93-51.98毫克（最高61.78毫克）。据中国原子能科学研究院化验，果肉含有10多种元素，镁、钙、铜、铁、铝、钛、锰、钒、磷、氯等，尤其是镁、钙、铜优于其它水果含量，自然钙含量高达2060单位，比其它柚类高出近4倍。所以，专家们一致认同“琯溪蜜柚综合性状居全国领先地位”。</span> \n</p>\n<span style=\"font-size:large;\"> \n<p>\n	<br />\n<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 专家对琯溪蜜柚的高度评价。中国农业大学食品科学与营养工程学院教授、博士生导师蔡同一认为，柚子独特芳香风味好，具有醒脾功效。中国营养协会常务理事、临床营养分会主任李淑媛认为：柚子全身都是宝，柚花可以提炼精油，供养容用；柚皮苷等属于黄酮类的成分，能降低胆固醇、保护心血管的作用。用柚子皮中的柚子苷成分可减少合成胆固醇西酶的活性，从而使胆固醇的合成减少，这对中老年人或者使用胆固醇脂肪代谢紊乱是委有作用的。著名健康教育专家洪绍光教授称：琯溪蜜柚不仅好吃，而且还能防病，在心脑血管预防和治疗方面的确有一定的功效。中国农业大学食品学院博士生导师韩雅珊认为：柚子具有调节人体新陈代谢之功用，有降压舒心、祛痰润肺、消食醒酒、降火利尿等作用。对脊椎症、脓肿症、便秘、糖尿病等患者有一定好处。</span>\n</p>\n<p>\n	<br />\n<span style=\"font-size:large;\"><strong><span style=\"font-size:14px;line-height:2;\">柚子的主功效：</span></strong><br />\n<span style=\"font-size:14px;line-height:2;\"> *维他命C：消除疲劳，养颜美容，预防感冒</span><br />\n<span style=\"font-size:14px;line-height:2;\"> *柚子酸：促进消化，消除疲劳</span><br />\n<span style=\"font-size:14px;line-height:2;\"> *橙皮苷：调节血压，预防血管老化和高血压,驱热功效</span><br />\n<span style=\"font-size:14px;line-height:2;\"> *果胶：促进新陈代谢&nbsp; &nbsp;&nbsp; &nbsp;养颜美容</span><br />\n<span style=\"font-size:14px;line-height:2;\"> *柚子香：能舒缓紧张情绪，使您精神放松，心情舒畅，同时亦可消除疲劳</span><br />\n<span style=\"font-size:14px;line-height:2;\"> 所以常食柚子及柚子食品，对人体的健康是非常有好处的。</span></span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\"><strong>食用方法</strong></span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 1、热饮：</span><span style=\"font-size:14px;line-height:2;\">将3至4茶匙O尼柚果茶(20G)加入温水（约８０ＣＣ）拌匀即可饮用冲调容易，冷热饮皆可。夏天更可预先冲调一大瓶放在冰箱内，方便随时饮用。</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 2、冰饮：冰饮请先以80℃热开水冲泡，冷藏,或者直接加冰块。</span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 3、可当作果酱，涂于面包上，味道可口。制作糕饼、可丽饼、果冻等材料，亦可加入红茶、优酪乳、养乐多、七喜或雪碧汽水同时饮用。 </span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp; 4、也可以稀释之后做成柚子果冻或柚子茶冻；也可以配合洋酒调成不同口味的鸡尾酒。 </span> \n</p>\n<p>\n	<span style=\"font-size:14px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 5、或可作为煮菜材料之一，如烹调柠檬鸡，咕噜肉等特别美味，亦为优选。</span> \n</p>\n<p>\n	<br />\n</p>\n</span>', 'images/201204/thumb_img/1565_P_1334338694330.jpg', 'images/201204/goods_img/1565_P_1334338694330.jpg', 'images/201204/source_img/1565_P_1334338694330.jpg', '1', '', '1', '1', '0', '0', '1334338693', '100', '0', '0', '0', '0', '0', '0', '1334616109', '1', '', '-1', '-1', '0', '0', '');
