INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1624', '21', '36501001624', '鲜糯玉米浆（白砂糖）', '+', '109', '3411', '', '1000', '3.000', '144.00', '120.00', '0.00', '0', '0', '0', '哈尔滨', '', '<p>&nbsp;</p>\r\n<p><span style=\"font-size:16px;line-height:2;\"><strong>产品介绍：</strong></span><span style=\"font-size:16px;line-height:2;\"><br />\r\n</span></p>\r\n<p><span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp; 玉米浆中富含膳食纤维、B族维生素、不饱和脂肪酸、玉米黄质、钙、硒、镁等微量元素，具有较高的营养价值和保健功效。</span></p>\r\n<p><span style=\"font-size:16px;line-height:2;\"><strong>配料：</strong>纯净水、鲜糯玉米浆、白砂糖、全脂乳粉、卡拉胶、单甘油脂肪酸酯、瓜尔胶、三聚磷酸钠、胡萝卜素、食用香精</span><br />\r\n<br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>食用方法：</strong>开盖即食</span><br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>保质期：</strong>6-12℃保质60天 </span></p>\r\n<p><span style=\"font-size:16px;line-height:2;\">&nbsp;</span><span style=\"font-size:16px;line-height:2;\">其主要营养成分及功能如下：</span><br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>(一)膳食纤维：</strong></span><br />\r\n<span style=\"font-size:16px;line-height:2;\">1 、防治便秘和压挤症。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">2 、预防癌症病变。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">3 、防治肥胖症。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">4 、降血压、降血脂。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">5、 通便利尿，清肠健胃，保护消化系统的健康</span><br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>(二)B族维生素：</strong></span><br />\r\n<span style=\"font-size:16px;line-height:2;\">1、VB1 ：保持循环、消化、神经和肌内正常功能；调整胃肠道的功能；构成脱羧酶的辅酶，参加糖的代谢；能预防脚气病。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">2、VB2：又叫核黄素。核典素是体内许多重要辅酶类的组成成分，这些酶能在体内物质代谢过程中传递氢，它还是蛋白质、糖、脂肪酸代谢和能量利用与组成所必需的物质。能促进生长发育，保护眼睛、皮肤的健康。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>(三)不饱和脂肪酸：</strong></span><br />\r\n<span style=\"font-size:16px;line-height:2;\">1、使胆固醇酯化，降低血中胆固醇和甘油三酯。 </span><br />\r\n<span style=\"font-size:16px;line-height:2;\">2、降低血液粘稠度，改善血液微循环。 </span><br />\r\n<span style=\"font-size:16px;line-height:2;\">3、提高脑细胞的活性，增强记忆力和思维能力。</span><br />\r\n<span style=\"font-size:16px;line-height:2;\"><strong>(四)玉米黄质：</strong></span><br />\r\n<span style=\"font-size:16px;line-height:2;\">1、预防老化性黄斑退化症（老年人失明的主要病因）；</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">2、预防白内障、内膜中层增厚、心脏病；</span><br />\r\n<span style=\"font-size:16px;line-height:2;\">3、抗癌作用，阻止癌细胞的扩散。</span><br />\r\n<br />\r\n<span style=\"font-size:16px;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp; 经常饮用玉米浆是一种对膳食纤维及多种营养成分良好的补充途径。由于将鲜糯玉米加工成玉米浆饮料携带方便、饮用便捷，口味好，可以随时补充体能及身体缺少的多种营养成分，达到代餐效果。</span><br />\r\n<br />\r\n<span style=\"font-size:16px;line-height:2;\">&nbsp;</span></p>', 'images/201205/thumb_img/1624_1338252424834.jpg', 'images/201205/goods_img/1624_1338252424834.jpg', 'images/201205/source_img/1624_1338252424834.jpg', '1', '', '1', '1', '0', '1', '1335477013', '100', '0', '0', '0', '0', '0', '0', '1336683522', '1', '', '-1', '-1', '0', '0', '');
