INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1692', '9', '36501001691', '天然蔓越莓', '+', '19', '3412', '', '1000', '0.200', '138.00', '115.00', '0.00', '0', '0', '1', '', '', '蔓越莓属杜鹃花科越橘属，特产于北美少数地区，是一种生长在矮藤上、小而圆、表皮富弹性的鲜红果子, 也有人称它为小红莓。蔓越莓需要以特殊的环境及气候条件栽培，全球的蔓越莓产区不到4万英亩，产量有限，又因其具有多种保健功能，从而具有“北美的红宝石”之美称。\n   研究显示，蔓越莓有助于防治胃部感染（主要指幽门螺杆菌感染）和泌尿道感染，其功效值得医学界重视。实验表明，蔓越莓萃取成份----前青花素能有效地防止细菌的黏附，也能有效防止耐药菌株黏附于人体组织细胞上。上述数据显示蔓越莓可对人体提供类似于天然抗生素的保护作用，而此种保护的作用机制不会增加抗生素产生的细菌耐药性。美国国家卫生研究院因此开展了“蔓越莓研究计划”，研究其抗细菌黏附的作用机制，结果显示蔓越莓不但可作为一种“天然抗生素”发挥抗菌作用，同时能够有效地遏止抗生素滥用所带来的一系列不良后果。\n   蔓越莓为何能具有防治幽门螺杆菌感染的功效?生化学家发现，蔓越苺会使存在于胃内的幽门螺杆菌不易附著在胃壁上，这种抗黏附机制使引发胃部感染的细菌无法在该部位繁殖而被排出体外。蔓越苺有抑制幽门螺杆菌黏附于胃上皮细胞的能力，而对已黏附在胃黏膜上的幽门螺杆菌，蔓越莓 仍可阻断细菌黏附，清除细菌。   \n   蔓越莓对泌尿道感染与心血管疾病也有效果。蔓越莓汁也被视为预防泌尿道感染的保健饮料，其作用机理依旧是抗细菌黏附机制。\n   此外，美国研究人員发现，蔓越莓具有很强的抗氧化作用，蔓越莓含有非常丰富生物黄酮等, 它们是一种强力的抗自由基物质。\n', 'images/201205/thumb_img/1692_1338330196167.jpg', 'images/201205/goods_img/1692_1338330196167.jpg', 'images/201205/source_img/1692_1338330196167.jpg', '1', '', '1', '1', '0', '0', '1337678419', '100', '0', '0', '0', '0', '0', '0', '1337678419', '1', '', '-1', '-1', '0', '0', '');
