INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1704', '9', '36501001703', '台湾破壁蜂花粉Taiwan Bee Pollen', '+', '21', '3412', '', '1000', '0.125', '816.00', '680.00', '0.00', '0', '0', '1', '', '', '破壁蜂花粉:世界上最多营养的天然食品 \n蜂花粉几乎包含了人体所需的基本营养物质，它也是自然界目前所知相当完美的保健食品之一。根据科学测定，花粉含有的营养素包括蛋白质（含必需胺基酸和非必需胺基酸）、醣类、脂肪酸 、维生素、矿物质（包含巨量和微量元素）、酵素和辅酵素、超氧化物歧化酶、核酸 、纤维素等一应俱全，远远超过任何营养品。\n\n营养素列表 \n\n?蛋白质包括游离胺基酸（磷酸乙醇胺、天门冬胺酸、羟脯胺酸、羟丁胺酸、丝胺酸、天门冬醯胺、麸胺酸、脯胺酸、甘胺酸、丙胺酸、瓜胺酸、缬胺酸、胱胺酸、甲硫胺酸、胱硫醚、异亮胺酸、亮胺酸、苯丙胺酸、β- 丙胺酸、β- 氨丁酸、γ- 氨丁酸、乙醇胺、氨、鸟胺酸、色胺酸、离胺酸、组胺酸、精胺酸等） \n?醣类包括果糖、葡萄糖、麦芽糖、蔗糖等 \n?脂类包括饱和脂肪酸（肉豆蔻酸、棕榈酸、硬脂酸、花生酸、二十二酸、二十四碳酸等），不饱和脂肪酸（棕榈油酸、油酸、亚麻油酸、α-次亚麻油酸、二十碳一烯酸等）和卵磷脂等 \n?14 多种维生素，包括维生素 A 、 B1 、 B2 、 B3 、 B5 、 B6 、 B12 、 C 、 D 、 E ( α-Tocopherol 、β-Tocopherol 、γ-Tocopherol 、δ-Tocopherol) 、H 、K 、叶酸、肌醇、胡萝卜素等　 \n?植物营养素，包括生物类黄酮、芸香苷、槲皮素等 \n?22 多种矿物质，包括碳、氢、氧、氮、镁、钙、钠、磷、钾、硫、氯等 \n?微量元素，包括铜、铁、硒、锰、矽、硼、铬、氟、钼、钒、锡等 \n?80 多种酵素和辅酵素，包括淀粉酶、脂肪酶、蛋白酶、磷酸酶、果胶酶等 \n?超氧化物歧化酶(SOD) \n?核酸，包括脱氧核醣核酸（ DNA ）和核醣核酸（ RNA ） \n?生长激素 \n?膳食纤维素，包括可溶性纤维和不溶性纤维 \n其中最有趣的真相是，蜂花粉是实验室无法复制的营养食品。即使复制的花粉已包含所有的营养物质，但当研究人员将蜜蜂饲料以人工复制的花粉取代蜂巢里的花粉后，蜜蜂却无法生存而死亡。这实验证明了蜂花粉含有实验室未能检验和确定的营养物质，也因这些“神秘”的独特物质，以致蜂花粉能在许多不同的健康状况上有神奇的疗效，也因为如此，蜂花粉被称为世界“浓缩营养宝库”！ \n\n', 'images/201205/thumb_img/1704_1338256442536.jpg', 'images/201205/goods_img/1704_1338256442536.jpg', 'images/201205/source_img/1704_1338256442536.jpg', '1', '', '1', '1', '0', '0', '1337678673', '100', '0', '0', '0', '0', '0', '0', '1337678673', '1', '', '-1', '-1', '0', '0', '');
