INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1718', '244', '36501001717', '[仿膳]盛世粽子礼盒', '+', '19', '3415', '', '1000', '1.000', '225.60', '188.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\"><strong>内配：</strong></span> \n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\"> 佛跳墙粽*2、莲蓉粽*2、桂花血糯粽*2、蜜枣粽*4、赤豆粽*4、咸鸭蛋一盒 <br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2.5;font-size:14px;\">&nbsp;&nbsp;&nbsp; 为皇帝提供的饮食，称为御膳。清王朝垮后，皇宫里的御厨也流落到了民间，这些厨师开了饭馆，因为他们所做的菜式，是仿照原来清宫的御膳，因此称为“仿膳”。</span> \n</p>\n<p>\n	<br />\n</p>', 'images/201205/thumb_img/1718_1338013409605.jpg', 'images/201205/goods_img/1718_1338013409605.jpg', 'images/201205/source_img/1718_1338013409605.jpg', '1', '', '1', '1', '0', '0', '1338013409', '100', '0', '0', '0', '0', '0', '0', '1338024004', '1', '', '-1', '-1', '0', '0', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1719', '244', '36501001718', '[仿膳]玲珑粽子礼盒', '+', '21', '3415', '', '1000', '1.000', '153.60', '128.00', '0.00', '0', '0', '1', '', '', '<span style=\"font-size:16px;\"><strong>内配：</strong></span><br />\n<br />\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 蛋黄鲜肉粽*2、莲蓉粽*2、蜜枣粽*4、红苞豆沙粽*4 为皇帝提供的饮食，称为御膳。清王朝垮后，皇宫里的御厨也流落到了民间，这些厨师开了饭馆，因为他们所做的菜式，是仿照原来清宫的御膳，因此称为“仿膳”。</span><br />\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	<img src=\"http://img.365chi.net:8080/images/upload/Image/image/20120528/20120528162640_13222.jpg\" alt=\"\" />\n</p>\n<p style=\"text-align:center;\">\n	<br />\n</p>\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 粽子（zòng zi）又称“角黍”、“筒粽”，是端午节汉族的传统节日食品，由粽叶包裹糯米蒸制而成。传说是为纪念屈原而流传的，是中国历史上文化积淀最深厚的传统食品。2010年12月，江西德安县宋代古墓出土了两个实物粽子，据考证，这是目前发现的世界上最早的实物粽。早在春秋时期就已经出现“筒棕”，直到现在的每年五月初，中国百姓家家都要浸糯米、洗粽叶、包粽子，这种风俗也流传到朝鲜、日本及东南亚诸国。</span><br />\n<br />\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp; 偶然翻读《本草纲目》，有“古人以菰叶裹黍米煮成尖角，如棕榈叶之形，故曰粽”的记载，再望书台上的台历，猛然感悟，过十天八天就要过端午节，吃粽子了。</span><br />\n<br />\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 关于粽子最早的记载是1600年前西晋新平太守周处所写的《风士记》云：“仲夏端午，烹鹜角黍。”200年后南朝梁文学家吴钧在《续齐偕记》中说：“屈原五月五日投泊罗而死，楚人哀之，遂以竹筒贮米，投水祭之。”于是以讹传讹，相沿成俗。可见人们对忠义之士感情之弥笃了。</span><br />\n<br />\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 粽子并非源自祭奠屈原之死，它的起源说法很多。最让人信服的是“包烹”之说，就是50万年前发明用火熟食时，为了适口，用树叶包裹食物放在火中煨后剥叶而食，这虽不叫粽子，却已有粽子的雏形。经过40万年的春秋更迭，进入石烹时代，先人们已能在地上挖坑，坑中垫兽皮，再注水其中，投入烧烫的石子使水沸腾，煨煮用植物叶子包裹的原料，直至成熟，这就更像现在的粽子。</span><br />\n<br />\n<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 粽子的出现如本文开头所说，但一说用于祭祖；又说为了纪念屈原殉难，两说都有道理。</span><br />\n<br />\n<br />', 'images/201205/thumb_img/1719_1338013414349.jpg', 'images/201205/goods_img/1719_1338013414349.jpg', 'images/201205/source_img/1719_1338013414349.jpg', '1', '', '1', '1', '0', '0', '1338013414', '100', '0', '0', '0', '0', '0', '0', '1338024004', '1', '', '-1', '-1', '0', '0', '');
