INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1081', '11', '36501001087', '稻花香礼盒', '+', '0', '45', '', '1000', '0.000', '183.60', '153.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">【产品介绍】：\n1、香！在稻田中就能味道香味，收货的水稻放在粮库里，整个仓库都有稻花香的气味，加工出的大米更是香，做饭时整个房间都有香味。整个是稻花香的最大的特点。\n2、甜！稻花香大米咀嚼起来，有一种微微甜的感觉，很好吃。\n3、吃起来，不咂，口感好！\n4、由于该种水稻的支链淀粉含量多等原因，不回生！可以用于做寿司米。\n5、本品包装高档、品质健康绿色，适合节假走亲朋、访良友作为随身伴礼携带。内部的包装采用真空包装，体积小、易携带、无杂质、长期保存不霉变，有效保存了大米中的各种营养物质。\n五常稻花香大米是黑龙江粳稻的一种，属于长粒，香米。稻花香米选自黑龙江五常的稻花香（2）号稻谷精研而成。富含营养物质，营养价值极为丰富。用天然无污染的乌苏里水浇灌，选择高端科技生物全脂活性菌有机肥料，延用传统栽培方式，采用生态环保包装加工而成，是目前最高品位大米，生长周期长，谷粒结实，米粒饱满，质地坚硬，色泽清白透明，饭粒油量，入口香软韧甜，浓郁醇厚。所以该种大米享誉全国，是目前最高档的大米之一。\n\n\n</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食用方法</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">煮饭（与水比例1：1.12）、煲粥。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>营养价值</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">大米中富含蛋白质、脂肪、碳水化合物、纤维素等人体维持身体机能所必须的营养物质，同时还含有铁、硒、锌、铜、锰等微量元素。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>保存注意事项</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">避免阳光直射，置阴凉干燥处</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">一个成年人每日粮食的摄入量以400克左右为宜，最少不能低于300克。\n 现在，许多健美教练员和运动员已经开始将早餐吃米饭看作是一种理想的能量补充法。原因是大米在人体内的消化较缓慢，更容易被人体吸收，且血糖反应平缓，使能量供应维持更长的时间。大米、小米等谷类食物含有的碳水化合物除了为人体提供能量外，还是B族维生素的主要来源，对头发也有很好的营养效果。很多爱美的女士为了保持好的身材，常常会不吃主食。研究发现，一日三餐吃全谷物大米的人最不容易肥胖。每天三顿都食用谷类大米食品的妇女，BMl指数(又叫体重指数)比少吃一顿全谷物食品的妇女低得多;男性的趋势也基本相似。事实证明谷物大米不但不会影响我们的身材，反而会让我们既拥有健康，又拥有苗条的好身材。\n 大米，除了有果腹之效，还具有相当多的药性，食用大米可预防和治疗疾病。从中医的角度看，大米性味甘平，有补中益气、健脾养胃、益精强志、和五脏、通血脉、聪耳明目、止烦、止渴、止泻的功效。</span></p><p><br /></p>', 'images/201204/thumb_img/1081_thumb_P_1334336248711.jpg', 'images/201204/goods_img/1081_P_1334336248334.jpg', 'images/201204/source_img/1081_P_1334336249023.jpg', '1', '', '1', '1', '0', '0', '1334336248', '100', '0', '0', '0', '0', '0', '0', '1334336248', '1', '', '-1', '-1', '', '', '');
