INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1090', '5', '36501001101', '有机香椿芽', '+', '0', '0', '', '1000', '0.000', '8.40', '7.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">香椿芽，又叫香椿头、香椿、香椿尖，被称为“树上蔬菜”更是“绿色食品”。每年春季清明后谷雨前，香椿树发的嫩芽既香椿芽可做各种菜肴。它不仅营养丰富，且具有较高的药用价值。香椿叶厚芽嫩，绿叶红边，犹如玛瑙、翡翠，香味浓郁，营养丰富远高于其他蔬菜，尤其是安徽太和沙河两岸所产的黑油椿更为宴宾之名贵佳肴，太和香椿历史悠久，也是中国香椿生产基地。\n编辑本段功效\n　　香椿是时令名品，含香椿素等挥发性芳香族有机物，可健脾开胃，增加食欲。香椿具有清热利湿、利尿解毒之功效，是辅助治疗肠炎、痢疾、泌尿系统感染的良药。它含有维生素E和性激素物质，能抗衰老和补阳滋阴作用，对不孕不育症有一定疗效，故有“助孕素”的美称。香椿含有楝素，挥发气味能透过蛔虫的表皮，使蛔虫不能附着在肠壁上而被排出体外。\n编辑本段适合人群\n　　健康人均可。\n编辑本段适用量\n　　每餐30～50克。\n编辑本段温馨提示\n　　香椿芽以谷雨前为佳，应吃早、吃鲜、吃嫩；谷雨后，其膳食纤维老化，口感乏味，营养价值也会大大降低。香椿为发物，多食易诱使痼疾复发，故慢性疾病患者应少食或不食。 　　需要注意的是，吃香椿前要用开水烫一下。有资料表明，平均每公斤香椿中含有30毫克以上的亚硝酸盐，老叶中更是高达每公斤53.9毫克，容易引发亚硝酸盐中毒，甚至诱发癌症。试验结果发现，用凉水洗过的香椿中，亚硝酸盐含量为每公斤34.1毫克，而用开水烫后仅为每公斤4.4毫克。所以，香椿还是用开水烫后再吃最安全。[2]</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>保存注意事项</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">常温下干燥.阴凉处</span></p><p><br /></p>', 'images/201204/thumb_img/1090_thumb_P_1334336273741.jpg', 'images/201204/goods_img/1090_P_1334336273306.jpg', 'images/201204/source_img/1090_P_1334336274360.jpg', '1', '', '1', '1', '0', '0', '1334336273', '100', '0', '0', '0', '0', '0', '0', '1334336273', '1', '', '-1', '-1', '', '', '');
