INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1084', '15', '36501001092', '北大荒绿野精品香菇', '+', '0', '45', '', '1000', '0.000', '36.48', '30.40', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">【商品介绍】：\n1、在产品种植、加工、包装、运输过程中，严格按照国家有机食品的执行标准、并通过中国有机食品认证，是一类真正源于自然、富营养、高品质的环保型安全食品。\n2、产品种植区域为北大荒绿色食品基地，黑土地带土地肥沃、水质优良，产品营养价值高于其他产区。\n\n</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食用方法</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">将产品泡发洗净，煮汤、热炒或者炖食均可。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>营养价值</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">香菇不但具有清香的独特风味，而且含有丰富的对人体有益的成分。据分析，每百克鲜香菇中含蛋白质12～14克，碳水化合物59.3克，钙124毫克，磷415毫克，铁25.3毫克，还含有多糖类、维生素Ｂ1、维生素Ｂ2、维生素Ｃ等。干香菇的水浸液中含有多种氨基酸、乙酰胺、胆碱、腺嘌呤等成分。香菇中还含有丰富的食物纤维，经常食用能降低血液中的胆固醇，防止动脉粥样硬化，对防治脑溢血、心脏病、肥胖症和糖尿病都有效。近年来，美国科学家发现香菇中含有一种“β－葡萄糖苷酶”，试验证明，这种物质有明显的加强机体抗癌的作用，因此，人们把香菇称为“抗癌新兵”。香菇还能抗感冒病毒，因香菇中含有一种干扰素的诱导剂，能诱导体内干扰素的产生，干扰病毒蛋白质的合成，使其不能繁殖，从而使人体产生免疫作用。\n \n    香菇性寒、味微苦，有利肝益胃的功效。我国古代学者早已发现香菇类食品有提高脑细胞功能的作用。如《神农本草》中就有服饵菌类可以“增智慧”、“益智开心”的记载。现代医学认为，香菇的增智作用在于含有丰富的精氨酸和赖氨酸，常吃，可健体益智。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>保存注意事项</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">避免阳光直射，置阴凉干燥处。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">香菇含有一种分子量为100万的抗肿瘤成分-----香菇多糖，含有降低血脂的成分-----香菇太生，香菇腺嘌呤和腺嘌呤的衍生物，香菇还含有抗病毒的成分------干扰素的诱发剂----双链核糖核酸，是不可多得的保健食品之一。香菇中含不饱和脂肪酸甚高，还含有大量的可转变为维生素D的麦角甾醇和菌甾醇，对于增强抗疾病和预防感冒及治疗有良好效果。经常食用对预防人体，特别是婴儿因缺乏维生素D而引起的血磷、血钙代谢障碍导致的佝偻病有益，可预防人体各种粘膜及皮肤炎病。香菇中所含香菇太生（lentysin）可预防血管硬化，可降低人的血压，从香菇中还分离出降血清胆固醇的成分（C8H1104N5，C9H1103N5）。香菇灰分中含有大量钾盐及其它矿质元素，被视为防止酸性食物中毒的理想食品。</span></p><p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334336251', '100', '0', '0', '0', '0', '0', '0', '1334336251', '1', '', '-1', '-1', '', '', '');
