INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1128', '25', '365010011272', '羌都红枣', '+', '0', '56', '', '1000', '0.000', '184.80', '154.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">“羌都红枣”出产自素有“华夏第一县”之称的新疆若羌县境内，地处巴音郭楞蒙古自治州东南部，北纬36°—41°23′， 是世界水果专家公认的“水果优生区带”，四周被沙漠和戈壁包围，沙漠干热高温与阿尔金山冷空气昼夜在此交锋，形成特大日温差，造就了中国精品红枣之乡。\n“羌都红枣” 独处千里茫茫戈壁沙漠之中，甜脆适口，香味浓厚，多食不厌，优质制干红枣含糖量高，色红、皮薄肉厚，紧凑密实，果肉细腻，清甜爽口，堪称枣中极品。\n羌都精品红枣天然挂干，呈长圆形，个头均匀，果形饱满、皱缩度小、易于储藏，表皮光滑洁净、呈暗红色、果面皱纹浅，红枣果肉紧密厚实、有韧性，食具粘性、味香甜、具有本品种独特的回味；维生素含量是苹果的50倍，梨子的100倍，水份含量≤20%。\n\n一、光热地理优势：800米左右最佳海拔，年近210天无霜期，塔克拉玛干大沙漠干热高温与阿尔金山雪域凉爽气流昼夜置换，形成高达27.8摄氏度以上日温差，独有的光热资源和巨大的昼夜温差，成就了若羌红枣异乎寻常的品质。与内地红枣比，若羌红枣不裂果、耐储存、不生虫，干枣含糖量高达68%，是别处红枣无法比拟的。\n二、 不凡品质优势：皮薄而久煮不裂，核小而摇之脆响，肉厚而坚实细腻、味甘且浓香爽口，色红而气韵高贵，形端而圆润饱满。\n    三、 神奇生灵优势：在滴水贵如金的沙漠之地，羌都红枣竟然超凡脱俗、生机勃发，这是大自然精气凝结成的神奇精灵。\n    四、 养生健体优势：昆仑支脉、阿尔金山山脉泉溪水富含多种人体所需矿物质，润育出羌都红枣强身健体延年益寿之奇效。“常食若羌‘羌都枣’，无须昆仑‘盗仙草’”。\n五、 绿色时尚优势：若羌绿洲深处千里无人烟的茫茫沙漠戈壁，背靠阿尔金山，宛如世外桃源。羌都红枣在树上自然挂干，无需任何连晒工艺，无病虫害，无须化肥农药，自成绿色食品。\n    六、 西域文化优势：吃着子母河畔、女儿国度、罗布老人、楼兰后裔献上的红枣，可尽情领略神秘的丝路风情、回味悠远的楼兰文化。\n\n   医圣张仲景的《伤寒论》和明朝李时珍的《本草纲目》中均有记载：红枣有润心肺、止咳定喘、补五脏、治虚损、调营卫、缓阴血、生津液、悦颜色、除肠胃邪气之功用；红枣主治腹气、平胃气、通九窍助十二经；补少气、少津液、身中不足；和百药、补血降压、补肾健脑。久服轻身、养颜、益发、延年。可谓“百果之冠”。 具有保肝、健脾、降低胆固醇、升高白血球、抗过敏等作用。\n在中药学里，红枣的应用可分为以下几种：\n    1.健脾益胃：脾胃虚弱、腹泻、倦怠无力的人，每日吃红枣七颗，或与党参、白术共用，能补中益气、健脾胃，达到增加食欲、止泻的功效；红枣和生姜、半夏同用，可治疗饮食不慎所引起的胃炎如胃胀、呕吐等症状。\n    2.补气养血：红枣为补养佳品，食疗药膳中常加入红枣补养身体、滋润气血。台大免疫学孙安迪博士大力提倡平时多吃红枣、黄耆、枸杞，能提升身体的元气，增强免疫力。\n    3.养血安神：女性躁郁症、哭泣不安、心神不宁等，用红枣和甘草、小麦同用〈甘麦大枣汤〉，可起到养血安神、舒肝解郁的功效。\n    4.缓和药性：红枣常被用於药性剧烈的药方中，以减少烈性药的副作用，并保护正气。如：「十枣汤」中，用大枣缓解甘遂、大戟、芜花等泻药的毒性，保护脾胃不受伤害。\n孕妇应经常食用红枣：增强母体免疫力、促进胎儿大脑发育、健脾益胃、安神定志、补血、降血压。\n【注意】：糖尿病人最好少食用，含糖量太高。\n</span></p><br /></p>', 'images/201204/thumb_img/1128_thumb_P_1334336553904.jpg', 'images/201204/goods_img/1128_P_1334336553420.jpg', 'images/201204/source_img/1128_P_1334336554067.jpg', '1', '', '1', '1', '0', '0', '1334336553', '100', '0', '0', '0', '0', '0', '0', '1334336553', '1', '', '-1', '-1', '', '', '');
