INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1168', '22', '36501001171', '手工牛轧糖（花生）', '+', '0', '0', '', '1000', '0.000', '30.00', '25.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">“牛轧糖”是大家喜爱的糖果。可是何以名之为“牛轧糖”？一般人往往不甚清楚。\n　　相传明代浙江考生“商辂”，某夜梦见自己跪于文昌殿前，祈求文昌帝君保佑考试顺利，供桌上备有笔墨纸砚、一盘花生和麦芽糖。这时文昌帝君突然显灵，对着供桌，手臂一挥，只见供桌上的花生自动去壳，一一飞入麦芽糖中，成了花生糖，而花生糖瞬间化为数头只牛，朝他飞奔过来。就在此刻，商辂梦醒了。\n　　解梦者指出，这都是好兆头。商辂闻之大喜，乃命家仆取米谷和麦芽糖，仿梦中情境，加入花生，制成硬糖，并且将之压成牛状的花生糖，与人分享。 \n　　后来，正式应考，连着参加乡试、会试、殿试，都荣获第一名，商辂可说是明代八十九位状元之中，唯一“三元及第”者。于是他衣锦还乡，大量制作花生糖，向文昌帝君还愿。此后，官运亨通，曾任吏部尚书兼大学士之一品官。久而久之，其他考生为祈求考运和官运，就效法他，以花生糖祭拜文昌帝君。 \n　　兹因典故中有“牛”，且商辂之“辂”，有“迎接”之意，故名之曰“牛辂糖”；又因“辂”与“轧”同音，“牛辂糖”逐渐写成“牛轧糖”。\n</span></p><br /></p>', 'images/201204/thumb_img/1168_thumb_P_1334336670795.jpg', 'images/201204/goods_img/1168_P_1334336671815.jpg', 'images/201204/source_img/1168_P_1334336671944.jpg', '1', '', '1', '1', '0', '0', '1334336670', '100', '0', '0', '0', '0', '0', '0', '1334336670', '1', '', '-1', '-1', '', '', '');
