INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1192', '22', '36501001196', '火麻黑蒜糖', '+', '0', '0', '', '1000', '0.000', '38.40', '32.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">黑蒜中含有的大蒜素具有广谱抗菌效果，它对几十种流行病毒及多种致病微生物都有杀灭作用。起杀菌作用的成分主要是大蒜素，还有一种白色油脂性液体“硫化丙烯”[（CH2CH2 H2）2S]。这种大蒜素即使稀释10万倍仍能在瞬间杀死伤寒杆菌、痢疾杆菌、流感病毒等。黑蒜的挥发性物质、浸出液及蒜素在试管内对多种致病菌都有明显的抑制或杀灭作用。这些含硫化合物对腐败真菌也有很强的抑制和杀灭作用，其作用强度相当于甚至强于化学防腐剂苯甲酸、山梨酸，是目前发现的天然植物中抗菌作用最强的一种。\n　　抗衰老，抗氧化neng力是普通大蒜的 39 倍\n　　抗氧化能力强\n　　人体衰老的实质就是被氧化，而黑蒜的超强的抗氧化的功能就是它能对较多病症产生非常好的效果的主要原因，同时也就对会造成身体多功能减弱的疾病产生非常好的保健治疗作用。\n　　增强免疫功能\n　　实验表明，黑蒜中的脂溶性挥发油能显著提高巨噬细胞的吞噬机能，有增强免疫系统的作用。大蒜素具有活化由糖脂质组成的细胞膜功能，能提高其渗透性，使细胞的新陈代谢加强，活力提高，机体免疫力随之加强。赖氨酸、丝氨酸都有提高免疫力的功能，维生素C也能增强人体的免疫力。黑蒜中含有的锌参与激素的合成，改善人体的免疫力。\n　　强力调节血糖水平\n　　黑蒜能影响肝脏中糖原的合成，减少其血糖水平并增加血浆的胰岛素水平。其中的蒜素能使正常人的血糖水平下降，黑蒜中还含有S-甲基半胱氨酸亚砜和S-烯丙基半胱氨酸亚砜，此含硫化物可抑制G-6-P酶NADPH，防止胰岛素破坏，有降血糖作用。黑蒜中的烯丙基二硫化物也有此作用。黑蒜中含有的生物碱，也具有降低血糖的成分，增加胰岛素的功能，更重要的是它对正常血糖值无影响。甘氨酸降低血液中的血糖值，防治糖尿病。异亮氨酸有促进胰岛素分泌、调节血糖的功能。\n　　研究人员在动物实验中用25g黑蒜汁喂养家兔进行葡萄糖耐量试验，测得黑蒜组的最大血糖下降值为 12.4%±1.2%，蒸馏水对照组则为1.8%±0.5%，说明黑蒜对控制血糖有明显效果。他们又给由四氧嘧啶所致糖尿病的大鼠经口摄取黑蒜提取物，也显示出黑蒜有降血糖作用，摄入2h后其血糖浓度降低17.9%～26.2%。\n　　黑蒜的调节血糖能力不是简单的控制，而是在保护胰岛素、主动将糖转化能能量，使糖尿病人拥有普通人一样的化糖能力，而不是药物降糖，从而决定了黑蒜非一般的降糖产品可比的功效。\n</span></p><br /></p>', 'images/201204/thumb_img/1192_thumb_P_1334336748991.jpg', 'images/201204/goods_img/1192_P_1334336748775.jpg', 'images/201204/source_img/1192_P_1334336748006.jpg', '1', '', '1', '1', '0', '0', '1334336748', '100', '0', '0', '0', '0', '0', '0', '1334336748', '1', '', '-1', '-1', '', '', '');
