INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1195', '22', '36501001199', '波波糖', '+', '0', '18', '', '1000', '0.000', '11.76', '9.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">波波糖，是清朝咸丰年间，当时的贵州安顺镇宁州知州，为在当地发展糖食，广向民间征集糖食制品。这时在钟鼓楼脚的刘家老曾祖刘兴汉认为镇宁六马盛产芝麻、糯米、麦子，于是决定以这几种农副产品作主要原料试制糖食，经过多次试验，几度失败，终于制成了酥、脆、香、甜、味美可口，别具一格的“饽饽糖”。\n\n波波糖的制作过程有：发、榨、熬、扯、起酥等十几道工序。它不仅好吃，经鉴定还含有大量维生素丙、葡萄糖和脂肪，营养丰富。并有润肺、止咳、化痰和助消化作用。原名叫“饽饽糖”，后以制作过程的主要工序特征犹如水波荡漾，就改名为“波波糖”。\n\n经当地官府品尝鉴定，赞不绝口，并用坛子封装送上司品尝，认定为佳品。曾有人赠联云：“镇宁城，钟鼓楼，既宏既高，高临全宇称魁首；刘记号，波波糖，又脆又香，香酥沁人誉名州。”从那以后。凡进京赶考的文人举子，过往的各方客商，各界知名人士，都要买上几封带走。这样，逐渐地驰名于全国。\n\n</span></p><br /></p>', 'images/201204/thumb_img/1195_thumb_P_1334336755804.jpg', 'images/201204/goods_img/1195_P_1334336755216.jpg', 'images/201204/source_img/1195_P_1334336755397.jpg', '1', '', '1', '1', '0', '0', '1334336755', '100', '0', '0', '0', '0', '0', '0', '1334336755', '1', '', '-1', '-1', '', '', '');
