INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1211', '26', '36501001215', '桂圆肉', '+', '0', '0', '', '1000', '0.000', '57.60', '48.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">桂圆因其种圆黑光泽，种脐突起呈白色，看似传说中“龙”的眼睛，所以得名。新鲜的龙眼肉质极嫩，汁多甜蜜，美味可口，实为其他果品所不及。鲜龙眼烘成干果后即成为中药里的桂圆。　</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">【科属与药用部分】本品为无患子科植物龙眼的假种皮。 　　【性味与归经】甘，温。入心、脾经。 　　【功效】补心安神，养血益脾。 　　性温味甘，益心脾，补气血；具有良好的滋养补益作用： 　　1、温补：小孩常感冒体质虚冷常尿床、记忆力不佳，多喝桂圆茶可增进脑力，改善虚冷体质。 　　2、丰胸：中医医说女人主血气，常吃桂园的女人，脸色红润、身材丰满，所以很多丰胸的补方，都以桂圆作为搭配。 　　3、安眠：中医说桂圆具有安神养心、补血益脾的功效，非常适合长期失眠者食用。 　　4、补血：能改善心血管循环、安定精神状况、舒解压力和紧张，桂圆含有丰富葡萄糖、蔗糖、蛋白质及多种维生素和微量元素，有良好的滋养补益作用。可用于治疗病后体弱或脑力衰退，妇女在产后调补也很适宜。</span></p><p><br /></p>', 'images/201204/thumb_img/1211_thumb_P_1334336799845.jpg', 'images/201204/goods_img/1211_P_1334336799950.jpg', 'images/201204/source_img/1211_P_1334336800945.jpg', '1', '', '1', '1', '0', '0', '1334336799', '100', '0', '0', '0', '0', '0', '0', '1334336799', '1', '', '-1', '-1', '', '', '');
