INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1284', '26', '36501001289', '可米小子南美洲鲍鱼果', '+', '0', '3', '', '1000', '0.000', '58.80', '49.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\"> 鲍鱼果具有一般坚果的重要特性：增强大脑记忆。而且果仁特别香而脆，它的外形特别像鲍鱼，所以谓之：鲍鱼果。外壳坚硬，果仁肥白香润，有香气，吃起来特别香美，余味绵绵，因此成为最受人们欢迎的坚果类食品，有“坚果之王”的称呼。\n    鲍鱼果营养丰富，果仁中除含有蛋白质、脂肪、胡萝卜素外，维生素B1、维生素B2、维生素E含量也非常丰富；果仁中含有人体所需的8种氨基酸，其中钙、磷、铁含量也高于其他坚果。吃的时候需要借助坚果钳子或者取仁小器。酥脆可口，回味香浓，是旅游休闲佳品和馈赠亲友之高档礼品。\n巴西坚果是当今已知富含有机硒素最高的植物，能够促进谷胱甘肽的合成。最新研究表明：巴西坚果对于解酒护肝的作用相当明显。\n鲍鱼果市场价25元/斤，巴西坚果价格比以往任何时候都要好。\n\n来源: 水果邦爱果者论坛www.shuiguobang.com 详情参考：http://bbs.shuiguobang.com/forum.php?mod=viewthread&tid=19370&highlight=</span></p><br /></p>', 'images/201204/thumb_img/1284_thumb_P_1334337051419.jpg', 'images/201204/goods_img/1284_P_1334337052921.jpg', 'images/201204/source_img/1284_P_1334337052276.jpg', '1', '', '1', '1', '0', '0', '1334337051', '100', '0', '0', '0', '0', '0', '0', '1334337051', '1', '', '-1', '-1', '', '', '');
