INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1295', '26', '36501001300', '椒盐巴旦木', '+', '0', '7', '', '1000', '0.000', '33.60', '28.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">巴旦杏营养价值很高，它的营养比同重量的牛肉高六倍。据化验，仁内含植物油５５－６１％，蛋白蛋２８％，淀粉、糖１０－１１％，并含有少量维生素Ａ、Ｂ１、Ｂ２和消化酶、杏仁素酶、杏仁成、钙、镁、钠、钾，同时含有铁、钴等１８种微量元素。巴旦杏仁是维吾尔人传统的健身滋补品，有人每日睡觉前细嚼十余粒，开水冲下，长期食用，夜间能通宵熟睡无梦，身体抵抗力显著增强，保持身强体壮。\n天然程度\n每逢初秋，当果园里的巴旦杏由绿变黄微带红晕，开始千裂成熟的时节，人们就络绎不绝地前去选购采摘，迅速将成熟的巴旦杏争购一空。\n除了盐椒巴旦木是用佐料炒过的以外，其他种类的巴旦木都纯天然的。\n养身意义\n1、健脑开窍安神：儿童食用可促进大脑发育，老年人食用即可安神，又可健脑，可用于老年性脑萎缩，老年性痴呆症的食疗品，同时也可作为一般的健身食品用。\n2、健肾作用：将巴旦杏制成酱食用，取得了良好的效果，可用于肾虚患者的辅助治疗，因为其口感好，无毒无副作用，乐于被患者接受，所以可长期服用。\n3、明目作用：维吾尔族人近视眼发病率明显较汉族人低，与本民族一些好的生活及饮食习惯有关。巴旦杏是维吾尔人经常食用的一种食品，从某一方面讲，在长期的食用中，巴旦杏发挥了它的明目作用。\n二、比较\n原味巴旦木\n原味巴旦木味道比较纯正，带一点木香口味，可以入药，仁味超过核桃和杏仁，有特殊的甜香风味。\n盐椒巴旦木\n盐椒巴丹木较硬,像一个杏核,但咬开里面的干果很甜很好吃,口感脆。和大杏仁的味道有点象，吃起来很香。口味比较浓重，咸咸的，比较适合青少年。\n纸皮巴旦木\n皮都是自然开口的，手一剥就开，比盐椒的味道要淡，比薄皮的味道要浓。\n薄皮巴旦木\n皮比纸皮的厚一点，但是没有纸皮的薄，味道像大杏仁。\n三、口感\n口感香脆、味道甘甜、富有甜香风味\n</span></p><br /></p>', 'images/201204/thumb_img/1295_thumb_P_1334337097850.jpg', 'images/201204/goods_img/1295_P_1334337098841.jpg', 'images/201204/source_img/1295_P_1334337098230.jpg', '1', '', '1', '1', '0', '0', '1334337097', '100', '0', '0', '0', '0', '0', '0', '1334337097', '1', '', '-1', '-1', '', '', '');
