INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1297', '26', '36501001302', '新疆杏肉', '+', '0', '8', '', '1000', '0.000', '27.36', '22.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">商品特点： \n酸酸杏干，富含纤维素，钾元素，维生素A，天然晾制~ \n新疆杏，具有果肉厚，核小，纤维多等优点而享誉海内外，产品具有杏子味外，还具有色泽饱满金黄，酸甜可口，营养丰富，多吃杏肉还可以消除人体内的硝酸基，可较好的软化血管，经常食用可开胃，防癌，美容皮肤，延年益寿，是居家馈赠亲友之佳品。 \n本产品采用产自新疆哈密地区 ， 肉厚核小的哈密杏为原料直接提取鲜杏消毒去核，经过多次烘烤制成 ，纯天然产品，未加任何防腐剂，一般5斤以上鲜杏才能烘制出一斤纯杏肉。 \n\n杏肉的功效： \n杏，降气、止咳、平喘、润肠通便、用于咳嗽、气喘、胸闷、痰多、血虚津枯、肠燥便秘。主治外感咳嗽、喉痹、防抗癌症等。新疆杏具有果肉厚、核小、纤维多等优点而享誉海内外。产品具有色泽金黄饱满，酸甜适口，营养丰富等优点。其药用、保健、滋补作用很大。很多中药方剂中都有杏果、仁的配伍。近代科学研究发现：杏具有护肤养颜的作用。国外医学研究机构最新发现杏中含有对癌细胞具有抑制、破坏作用成份，对癌症有一定疗效。杏果营养丰富，肉鲜甜软，未熟果实中含黄酮类较多。黄酮类有预防心脏病和减少心肌梗死的作用。因此，常食杏干，对心脏病患者有一定好处。新疆杏具有果肉厚、核小、纤维多等优点而享誉海内外。 \n\n杏肉营养： \n【杏味】酸、甘、温和，有小毒。果肉含糖、蛋白质、钙、磷、胡萝卜素、硫胺素、核黄素、尼克酸及维生素C；苦杏仁含苦杏仁甙、酶及脂肪油。甜杏仁含苦杏仁甙、脂肪油、糖分、蛋白质、树脂、扁豆甙、杏仁油。杏有生津止渴、润肺定喘功效。适用于口燥咽干、肺燥干咳、喘促气短等症。用于肺结核的潮热、阴虚所致的五心烦热，均可收到疗效。 \n\n</span></p><br /></p>', 'images/201204/thumb_img/1297_thumb_P_1334337104866.jpg', 'images/201204/goods_img/1297_P_1334337104129.jpg', 'images/201204/source_img/1297_P_1334337104774.jpg', '1', '', '1', '1', '0', '0', '1334337104', '100', '0', '0', '0', '0', '0', '0', '1334337104', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1298', '26', '36501001303', '原味鹰嘴豆', '+', '0', '8', '', '1000', '0.000', '23.76', '19.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">鹰嘴豆的营养成分是较全面的。据印度等国对373个品种进行的分析发现： 优质的蛋白营养来源：\n\n\n粗蛋白含量为12.4-31.5%，平均为23%，除含硫氨基酸较少外，其他必需氨基酸均可满足成人的需要蛋白质效率比值（PER）测定表明，鹰嘴豆为2.64，与大豆（2.55）相近似，但显著高于蚕豆（Horse bean）。\n\n\n按蛋白质净利用率（NPU）计，依次为鹰嘴豆、大豆、豌豆、菜豆、利马豆 其蛋白质消化率（TG）、生物效价（BV）分别为85%-89%、83%-85%，高于豇豆和绿豆。 这些测定说明，鹰嘴豆的蛋白质含量和质量都是比较高的，\n\n\n优质的油脂来源： 而且它还含有碳水化合物63.5%、脂肪5.3%以及多种矿物质和维生素等。特别是脂肪中含有油酸52.1%、亚油酸38%，这两种不饱和脂肪酸高达90.1%（迪西类型），比大豆高出10个百分点以上，因而是优质脂肪的一个有效来源。 矿物质含量丰富 在矿物质方面，除含有较多的钙、磷等元素外，每百克籽粒中含有铁5-23.9毫克，而且其利用率可达91%，高于其他豆类。\n\n\n鹰嘴豆的主要功能\n\n\n⒈预防糖尿病、高血脂；\n\n\n⒉提高人体免疫力；\n\n\n⒊补钙铁锌铜微量元素及各种维生素\n\n\n⒋调节血糖、补中益气。\n\n\n鹰嘴豆含有的十种氨基酸里，人体必需的8种氨基酸全部具有，而且含量比燕麦还高出二倍以上，这对儿童智力发育，骨骼生长及老年人强骨健身作用是相当大的。 富含有的人体不能合成但又必须的亚油酸以及纤维素，亚油酸是衡量事物营养的重要标志之一，而且是抑制糖尿病的主要成分之一。 纤维素有降低血糖、胆固醇的作用，这就是常食鹰嘴豆有助于治疗糖尿病的原因之一。鹰嘴豆常见的钙、镁、铁、锌等微量元素也很高，这对降低血清脂水平，降低胆固醇，加强中老年人中枢神经系统的抑制和心血管的保护有很大作用，对高血压、心脏病有疗效。\n</span></p><br /></p>', 'images/201204/thumb_img/1298_thumb_P_1334337108277.jpg', 'images/201204/goods_img/1298_P_1334337108645.jpg', 'images/201204/source_img/1298_P_1334337108290.jpg', '1', '', '1', '1', '0', '0', '1334337107', '100', '0', '0', '0', '0', '0', '0', '1334337107', '1', '', '-1', '-1', '', '', '');
