INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1301', '26', '36501001306', '野生天麻', '+', '0', '11', '', '1000', '0.000', '123.60', '103.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">为兰科植物。其植物为一株肉质独苗，黄红色。它没有兰草的绿叶，没有牡丹的丽花，没有玫瑰的芳香，但在她的根部却长着稀有珍贵的天麻。  波密野生天麻\n天麻的神奇在于它的周围始终有云雾缭绕。天麻靠密环菌供给养料，密环菌适宜在潮湿的土壤中繁殖，而潮湿的土壤由于水的蒸发而在其周围形成雾罩。天麻为稀为名贵的药用植物，其块茎是平肝息风的良药。 　　据化验，天麻中含有“香英兰醇”和维生素A，它对肝风引起的头痛有特效。除此之外，还可以益气、祛风湿、强筋骨。主治高血压、头痛眩晕、口眼歪斜、肢体麻木、小儿惊厥等症。 　　块茎天麻呈长椭圆形，略扁，皱缩而弯曲。一般两端钝圆，上端有时带有枯干残茎，下端有凹陷呈圆盘状的根痕,外表呈黄白色、黄棕色或黄褐色，全身多纵沟，有油点状的须根痕组成横 向环纹。质坚而紧密，不易折断。横切面为牙白色或棕黄色，为半透明体，味略甜带辛中药材 　　野生天麻是一种珍贵的中药材。洞口县山区的原始林木枝繁叶茂，气候温和，夏季较凉爽、冬季又不十分严寒，适合天麻生长。　 </span></p><br /></p>', 'images/201204/thumb_img/1301_thumb_P_1334337119535.jpg', 'images/201204/goods_img/1301_P_1334337119089.jpg', 'images/201204/source_img/1301_P_1334337120248.jpg', '1', '', '1', '1', '0', '0', '1334337119', '100', '0', '0', '0', '0', '0', '0', '1334337119', '1', '', '-1', '-1', '', '', '');
