INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1317', '26', '36501001322', '玫瑰酱', '+', '3', '0', '', '1000', '0.000', '32.16', '26.80', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\">玫瑰酱不仅颜色漂亮，味道芳香，而且美容效果非常棒。除了上面我们提到过的玫瑰酱能让人体香气四溢外，还可以除滋养容颜，去掉脸上的斑斑点点。一些女孩子脸上长雀斑，坚持吃一段时间的玫瑰酱，就会收到意外的惊喜。 玫瑰酱的这一功用其实都是玫瑰花和红糖的功劳。玫瑰花有活血化瘀的作用，自然能清毒美容；而红糖具有“温而补之，温而通之，温而散之”的温补作用，能够补血、活血、通瘀，人体一旦血脉通畅，内毒可以尽快清除排出，无法滞留，自然斑类的东西也就不会在脸上生长了。　　此外，玫瑰花酱在治疗妇科疾病方面，也有上佳表现。 比如许多女性会在月经来时或者月经前后，出现下腹部阵痛、痛经。这个时候，不如自制或者到超市上买些玫瑰酱来吃，即可以享受美味，又能够有效地减轻痛经的症状。　　如今，人们经常到麦当劳、肯德基去吃饭，他们总会给一些蕃茄酱佐食。与蕃茄酱比较起来，我们自制的玫瑰酱可谓酱中的极品——口感好，治病美容，而且是纯天然绿色食品，没有添加剂。所以，喜欢吃美食的女人空闲时不妨动手做点玫瑰酱，让日常生活更多些玫瑰花的色与香，更多些健康。23 </span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1317_thumb_P_1334337179117.jpg', 'images/201204/goods_img/1317_P_1334337179803.jpg', 'images/201204/source_img/1317_P_1334337179984.jpg', '1', '', '1', '1', '0', '0', '1334337179', '100', '0', '0', '0', '0', '0', '0', '1334337179', '1', '', '-1', '-1', '', '', '');
