INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1326', '7', '365010013256', '杜仲茶', '+', '0', '0', '', '1000', '0.000', '13.44', '11.20', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">杜仲茶以杜仲植物杜仲（Eucommia ulmoides Oliv）的叶为原料，在杜仲叶生长最旺盛时，或在花蕾将开放时，或在花盛开而果实种子尚未成熟时采收，以做杜仲茶。经传统茶叶加工方法制作而成的健康饮品东方神韵杜仲茶，品味微苦而回甜上口，常饮有益健康，睡前喝一杯特好，无任何副作用，保健价值极高，饮用方便。</span></p><br /></p>', 'images/201204/thumb_img/1326_thumb_P_1334337431737.jpg', 'images/201204/goods_img/1326_P_1334337431290.jpg', 'images/201204/source_img/1326_P_1334337432529.jpg', '1', '', '1', '1', '0', '0', '1334337431', '100', '0', '0', '0', '0', '0', '0', '1334337431', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1327', '7', '365010013263', '安化黑茶[千两茶]', '+', '0', '66', '', '1000', '0.000', '60.00', '50.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">安化黑茶因产自中国湖南安化县而得名。是中国古代名茶之一，上个世纪50年代曾一度绝产，以至于默默无名。2010年，湖南黑茶走进中国上海世博会，成为中国世博会十大名茶之一。安化黑茶再度走进茶人的视野，成为茶人的新宠。其特色的千两茶，堪称一绝。</span></p><br /></p>', 'images/201204/thumb_img/1327_thumb_P_1334337434524.jpg', 'images/201204/goods_img/1327_P_1334337434178.jpg', 'images/201204/source_img/1327_P_1334337434698.jpg', '1', '', '1', '1', '0', '0', '1334337433', '100', '0', '0', '0', '0', '0', '0', '1334337433', '1', '', '-1', '-1', '', '', '');
