INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1334', '7', '36501001333', '决明子茶', '+', '0', '0', '', '1000', '0.000', '11.76', '9.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">决明子茶微炒后的决明子浸泡代茶饮。以10g～20g置杯中，开水浸泡，色黄清香，味道甘苦，别有风味。决明子茶是豆科草本植物的成熟种子，味苦、甘而性凉，清肝明目、润肠通便。现代药理研究认为，决明子富含大黄酚、大黄素、决明素等成分，具有降压、抗菌和降低胆固醇的作用。将单味炒决明子15克，直接泡茶饮用，直至茶水无色。老年人饮用决明子茶不仅有助于大便通畅，还能起到明目、降压、调脂等保健功能。</span></p><br /></p>', 'images/201204/thumb_img/1334_thumb_P_1334337463664.jpg', 'images/201204/goods_img/1334_P_1334337464235.jpg', 'images/201204/source_img/1334_P_1334337464479.jpg', '1', '', '1', '1', '0', '0', '1334337463', '100', '0', '0', '0', '0', '0', '0', '1334337463', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1335', '7', '36501001334', '罗汉果菊花茶', '+', '0', '69', '', '1000', '0.000', '10.56', '8.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">菊花具有清热去火,疏散风热、清肝明目的功效。用于感冒风热、目赤肿痛、眩晕、高血压等症。\n　　罗汉果是驰名中外的广西特产药材。味甘，性凉，具有清热润肺、止咳、清暑解渴、润肠通便的功效。可用于治疗伤风感冒、咳嗽多痰、暑热胃热、便秘、慢性咽喉炎、慢性支气管炎、口干舌燥等病症。\n　　具有治疗和缓解伤风感冒症状、清热润肺、清肝明目、消暑解渴、润肠通便的功效，且一年四季都适合饮用。\n　　罗汉果含有丰富的甜味素和多种维生素，其甜度是砂糖的300－400倍，热量却极低。因为罗汉果所含的大量糖份不是葡萄糖，而是“萜配糖体”及少量果糖。由于小肠不会吸收萜配糖体，而被完全排出体外，因此自然不会被转化成热量。萜配糖体亦能发挥像纤维一样的作用，减慢葡萄糖到达小肠的时间，进食后血糖值不会急升，因而适合喜甜食但想减肥，又需要控制糖分摄取量的人。\n　　另外，购买罗汉果时，应挑选个大质坚，形圆色褐，摇不响，壳不破，不焦，味甜而不苦者。烤好的罗汉果色泽黄褐或者黄褐中略带藏青，若颜色焦黑，则说明烘烤过度，味道就会发苦。</span></p><br /></p>', 'images/201204/thumb_img/1335_thumb_P_1334337467949.jpg', 'images/201204/goods_img/1335_P_1334337468665.jpg', 'images/201204/source_img/1335_P_1334337468496.jpg', '1', '', '1', '1', '0', '0', '1334337467', '100', '0', '0', '0', '0', '0', '0', '1334337467', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1336', '7', '36501001335', '通便茶', '+', '0', '69', '', '1000', '0.000', '9.00', '7.50', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">便秘不仅可以诱发肛肠疾病,使肠道内毒素蓄积量不断增加，还会造成腹胀、体形肥胖变形,并出现色斑、皮肤粗糙、痤疮等。常便秘可导致肛结直肠并发症，比如：粪性溃疡、肠穿孔、结肠憩室肠梗阻、胃肠神经功能紊乱（如食欲不振、腹部胀满嗳气、口苦、肛门排气多等）。长期的便秘更可能诱发癌变。所以请您一定要引起重视，及早检查治疗。\n　　长期服用缓泻药之类易形成依赖性，使便秘越来越严重。同时还会导致营养物质的丢失，引起贫血、水电解质失衡导致体重过低、抵抗力下降、营养不良等并发症。如果经常或大量摄入这些药物,也可扰乱水和电解质的平衡.长期服用药物也会引起其他肠道病变。\n　　通便茶清肠降脂,清除体内垃圾,全面清除宿便,解决反反复复的便秘问题.服用后血液及皮肤充分析离,排除有害毒素。长期饮用清肠养生,抗衰延年。</span></p><br /></p>', 'images/201204/thumb_img/1336_thumb_P_1334337471213.jpg', 'images/201204/goods_img/1336_P_1334337471502.jpg', 'images/201204/source_img/1336_P_1334337472602.jpg', '1', '', '1', '1', '0', '0', '1334337471', '100', '0', '0', '0', '0', '0', '0', '1334337471', '1', '', '-1', '-1', '', '', '');
