INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1350', '7', '36501001350', '贵州毛峰', '+', '0', '73', '', '1000', '0.000', '216.00', '180.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">毛峰又称毛尖，是细嫩烘青的统称。指绿茶初制中形成条索细紧，露茸毫的嫩烘青。在小叶种地区制的毛峰，外形细紧，茸毫披露，显芽锋，汤色明亮，香气清高，滋味醇爽，叶底嫩绿明亮；大叶种制的，外形较肥壮，显露毫尖，色泽较黄或暗绿，香味较厚实，叶底肥嫩露芽。\n\n审评毛峰茶，在外形上应注重干茶色泽与嫩度。色泽嫩绿的为上品；茶绿的为中档；暗绿的为下档。条索紧结的，色泽深暗的多。毛峰在商品茶中色泽绿的价值大于色泽深暗的，故在审评时要重视色泽的绿翠程度。嫩度应是一芽一、二叶占多数，如果是一芽三叶占主体，应作大宗烘青对待。对香味的审评应注重香气的清香与滋味的醇爽程度。\n　　\n特性：外形硕壮成条，银毫遍布全身，色泽黄绿隐翠；叶底嫩绿呈黄，香气清高幽远，滋味甘醇鲜爽。</span></p><br /></p>', 'images/201204/thumb_img/1350_thumb_P_1334337520418.jpg', 'images/201204/goods_img/1350_P_1334337520312.jpg', 'images/201204/source_img/1350_P_1334337520353.jpg', '1', '', '1', '1', '0', '0', '1334337519', '100', '0', '0', '0', '0', '0', '0', '1334337519', '1', '', '-1', '-1', '', '', '');
