INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1431', '7', '36501001437', '日本玉露茶（进口茶） 粹系列方听', '+', '0', '105', '', '1000', '0.000', '309.60', '258.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">日本玉露保留着自我国唐代流传过去的“蒸青”绿茶制作技艺，并通过不断改进成为日本茶中的高级精品，素有“日本国茶”的美誉。玉露茶树栽培方法独特，茶树开始发芽时，茶农就会搭起稻草扎成的蓬，阻挡阳光直射树顶，而遮荫的特殊光合作用，会让鲜叶色泽更加鲜绿和厚重，氨基酸等的鲜美成分会大幅度提升。下雨时，雨水会将稻草特有的香气渗透进茶叶里。\n\n　　采摘时，茶农采用传统的手摘方法将茶叶采下，迅速送到工厂，约蒸30秒以保持风味和阻止发酵，然后用热空气使茶叶变软，挤压干燥直至含水量降到原来的30%。重复揉捻，使茶叶变成纤细暗绿的针状，然后挑拣出茶柄和老叶，再干燥。\n\n　　玉露茶香清幽，甘甜柔和，茶汤翠绿，气味上更青绿，有着不食人间烟火的仙气。\n\n　　使用铁壶煮水可以软化水质，用这样的水冲泡玉露茶可以提升口感。除了正常的冲泡方法，玉露还可以冰饮，即用冰块代替水，在玉露上面放上适量的冰块，让冰块慢慢自然融化后，就可以品到独一无二的“冰出玉露”的鲜美味道。\n\n　　像红酒一样，玉露绿茶在成熟陈化的过程中更加醇厚、甘醇，据说可以保存十年之久。新茶和窖藏数年的茶，味道截然不同。玉露最好放在０至１０度温度中保存，木制冷库保存效果更好。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334337543', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1432', '7', '36501001438', '日本煎茶（进口茶） 融系列圆听', '+', '0', '105', '', '1000', '0.000', '237.60', '198.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">融系列圆听日本煎茶是中茶世界茶园系列中的高规格产品，自饮、送礼均属上佳。世界五百强的品味，中华老字号的承诺。国际优秀茶品，体验纯正煎茶口味。\n\n日本的煎茶采用的是温和式的熏蒸法，经过这样处理的绿茶，气味上更青绿一些，并且保持了茶叶的本色特征。在茶成分的保存上，较中国式的锅炒制来得好，含有大量的抗氧化成分儿茶素，和能使肌肤洁白的维生素C，以及防止皮肤粗糙的维生素A。儿茶素及维生素等营养素，依茶类的不同，含量差异也极大，经研究发现，煎茶的维生素C为每100克含250毫克。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334337543', '1', '', '-1', '-1', '', '', '');
