INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1435', '7', '36501001441', '茉莉金钩 便携装', '+', '0', '105', '', '1000', '0.000', '81.60', '68.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">茉莉花茶，又叫茉莉香片，有“在中国的花茶里，可闻春天的气味”之美誉。茉莉花茶是将茶叶和茉莉鲜花进行拼和、窨制，使茶叶吸收花香而成的，茶香与茉莉花香交互融合，“窨得茉莉无上味，列作人间第一香。”茉莉花茶使用的茶叶称茶胚，多数以绿茶为多，少数也有红茶和乌龙茶。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334337543', '1', '', '-1', '-1', '', '', '');
