INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1439', '7', '36501001445', '天尖 简装', '+', '0', '105', '', '1000', '0.000', '42.00', '35.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">安化黑茶的制作原料均为黑毛茶，黑毛茶又按等级分为“芽尖、白毛尖、天尖、贡尖、乡尖、生尖、捆尖”七类，其中以“芽尖”为极品，但因数量极少，未能成为市场交易的商品，故在时下市场流通的黑茶产品中，以天尖茶为最佳。\n　　天尖茶古时为茶商馈送达官、富商、亲友的高贵礼品，清道光年间（1825年前后）被列为贡品，专供皇室饮用，现成为南方有钱人的一种饮用时尚，该茶既可以泡饮，也可煮饮；既适合清饮亦适合制作奶茶，特别在南方各茶馆煮泡壶茶，家庭煎泡冷饮茶，很合时宜。现在全国许多省、市饮用天尖茶已成为一种潮流，特别是南方的广州、西北的西安。\n　　天尖茶采用谷雨时节的鲜叶加工而成，色泽乌黑、口感甘爽、独具纯正松烟香、滋味醇厚、汤色深黄明亮、叶底匀齐尚嫩、且品质稳定，长期饮用具有减肥、降脂、降血、提高机体免疫力等功效。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334337543', '100', '0', '0', '0', '0', '0', '0', '1334337543', '1', '', '-1', '-1', '', '', '');
