INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1460', '7', '36501001472', '维恩淇巧克力情迷鸡尾酒', '+', '0', '77', '', '1000', '0.000', '216.00', '180.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">新西兰调配的VnC 鸡尾酒用全世界最好的原材料、精义的酿酒工程、最新鲜的水果精心制成的。不但对身体健康大有帮助，在今天繁忙压力生活中，它也是品味生活、时尚的至高境界。\n鸡尾酒是想象力的杰作，它的名字、颜色、口味，都充满着惊喜和魅惑，让人无法抗拒。它鲜艳亮丽的外形和独具个性的风格愈来愈受到世界各国消费者的欢迎和喜爱。\n鸡尾酒一词已经渗透到了世界的每一个角落，它之所以长盛不衰，主要在于其本身的魅力。\n早在1806年5月13日美国发行的一本杂志上就提到鸡尾酒，经过近2个世纪的演变，今天，鸡尾酒不仅渗透到世界的每个角落，而且其新的内涵也得到了大家的共识：具有滋补，提神功能，并能使人感到爽洁愉快的浪漫饮品。\n大多数人都认为鸡尾酒的女人酒，可恰恰相反的是最初的鸡尾酒，更多是为了满足男人们对于辛辣口感的需要。只是后来随着鸡尾酒在各种社交场合的广泛出现和使用，为了满足那些不能承受酒精深度过高的饮用者的要求，才逐渐产生出了适合女性口味的带有甜味的鸡尾酒。当然，女泩对于鸡尾酒的喜爱是远远高于男性的，那是因为女性天生对色彩很敏感，所以很自然的为这些漂亮颜色所迷醉。\n许多人之所以喜欢鸡尾酒，是因为它华丽的外表和浪漫的感觉， 也因为那种，似醉微熏，欲拒还迎的美，象极了爱情的感觉。品尝新西兰VnC鸡尾酒，就是亲生体验至高境界的艺术品。\nVnC 鸡尾酒无任何添加物、无防腐剂、无加颜色、无加香料、无加糖。采用天然、无公害、最新鲜当天丰收的水果，必须当天扎汁。新西兰VnC 鸡尾酒不但口感特好，它对身体的好处是降低血脂肪、有效的预防心脏病、延缓衰老、美容、还可预防某种癌疹、含有充分的维他命C 。\n所有在新西兰VnC 鸡尾酒内的成分，是在世界所有国家均公认绿色食品，包含美国及欧洲。\n新西兰VnC 鸡尾酒所用的伏特加是用新西兰纯净的天然水，然后经过活性炭过滤，取出任何杂质，所以它的口感特好，最适合调配鸡尾酒。\n</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">冰镇，摇晃，倒在冰块上。 来自长白云故乡新西兰的纯天然水果鸡尾酒，从全球各地精选最优质的新鲜水果和酒精饮料，无任何添加剂，口感天然，纯净，纯正。</span></p><p><br /></p>', 'images/201204/thumb_img/1460_thumb_P_1334337552238.jpg', 'images/201204/goods_img/1460_P_1334337552507.jpg', 'images/201204/source_img/1460_P_1334337553543.jpg', '1', '', '1', '1', '0', '0', '1334337552', '100', '0', '0', '0', '0', '0', '0', '1334337552', '1', '', '-1', '-1', '', '', '');
