INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1462', '7', '36501001474', '四季花海', '+', '0', '79', '', '1000', '0.000', '42.00', '35.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">产品简介\n四季花海选用的茶种“京菊”更是拥有“京城第一菊”的美称。本茶品选用的“京菊”茶菊系列品种是北京市农林科学院黄丛林研究团队经过多年的潜心研究，培育出来适合北京地区种植的优质品种，与北京市花相得益彰。由于京菊在种植上具有抗旱耐瘠薄、抗病虫害、花期就越等特点，所以京菊在种植过程中不施化肥，不打农药，保持无污染的生产环境，是纯自然的绿色饮用品。\n \n营养价值\n经北京市营养源研究所进行成分分析，营养保健成分丰富且含量高：每100克干重含总氨基酸14.47克、蛋白质23.8克、钙974.99毫克、维生素C45.2毫克、胡萝卜素43.3毫克、叶酸110毫克，总黄酮含量百分之4.88。其米菊和胎菊更是茶菊中的极品，色相好，营养保健成分含量高，冲泡两昼夜而茶色不变。\n \n来源介绍\n京菊的生长环境特点——高海拔、强日照：营养成分高；高纬度冷凉气候：色泽鲜艳、花型优美、晶相好；自然环境生长：原汁原味。</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">泡饮菊花茶时，最好用透明的玻璃杯，每次放上四五朵，再用沸水冲泡，大约二十秒后将水倒掉，再次将开水冲入，两分钟即可饮用，如能加入适量冰糖和蜂蜜，则风味和疗效更佳。菊花可与枸杞、决明子、红枣、绿茶等同泡。</span></p><p><br /></p>', 'images/201204/thumb_img/1462_thumb_P_1334337561052.jpg', 'images/201204/goods_img/1462_P_1334337562906.jpg', 'images/201204/source_img/1462_P_1334337562371.jpg', '1', '', '1', '1', '0', '0', '1334337561', '100', '0', '0', '0', '0', '0', '0', '1334337561', '1', '', '-1', '-1', '', '', '');
