INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1492', '9', '36501001491', '蕨麻王', '+', '0', '8', '', '1000', '0.000', '46.56', '38.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">蕨麻，为蔷薇科植物鹅绒委陵菜(Potentillaanserina)的块根，又名人参果，藏语名戳玛。产于我国东北、华北和西北各地。西藏全境内均有分布，牧区最多，并遍布于青海省海拔3000多米的草原上。藏族人称蕨麻为“卓老沙僧”，味甘美，含有大量淀粉、蛋白质、脂肪、维生素，长期食用能延年益寿。蕨麻含有很多生物活性物质，如蕨麻多糖、鞣质、蕨麻素和野鸦椿酸等。蕨麻根中尚含胆碱、甜菜碱、组胺酸、琥珀酸、黄酮类化合物等。蕨麻含有粗蛋白10.08%、脂肪1.14%、膳食纤维15.18%、灰分3.52%、还原糖1.90%、总糖12.47%、淀粉18.02%。氨基酸种类齐全，不但含有多种氨基酸(17 种)，而且含有人体所需的8 种必需氨基酸，其中精氨酸的含量尤为突出，达到16.6%，这是任何其他食物所达不到的。每100 g 蕨麻中无机元素Ca、P、Mg、Fe、Zn 的含量分别为229.06 mg、370.07 mg、141.13 mg、21.23 mg、20.49 mg，都较一般根茎类食品高出很多倍。维生素C 含量特别高，每100 g 中可达19.2 mg。由此可见，蕨麻的营养价值很高。</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食用方法</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">用温水洗净，煮稀饭或做八宝粥食用，或单独煮熟食用</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>营养价值</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">蕨麻含有很多生物活性物质，如蕨麻多糖、鞣质、蕨麻素和野鸦椿酸等。蕨麻根中尚含胆碱、甜菜碱、组胺酸、琥珀酸、黄酮类化合物等</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>保存注意事项</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">置阴凉干燥处,谨防受潮</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">益气补血、健胃补脾、生津止渴</span></p><p><br /></p>', 'images/201204/thumb_img/1492_thumb_P_1334338389100.jpg', 'images/201204/goods_img/1492_P_1334338389179.jpg', 'images/201204/source_img/1492_P_1334338389189.jpg', '1', '', '1', '1', '0', '0', '1334338389', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
