INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1506', '9', '36501001508', '油焖香椿', '+', '0', '82', '', '1000', '0.000', '90.00', '75.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">油焖香椿：原为沂蒙山区民间的一种野菜食用方法。他采用沂蒙山深处天然林中的乔木树木----香椿树的嫩芽，利用民间独特的传统工艺制作而成。具体做法为：一、将采摘的新鲜香椿嫩芽洗净，晾至半干（手挤不出水为准），加辣椒粉（1000克香椿芽加20克辣椒粉）、生姜（1000克香椿芽加30克生姜） 、盐（适量）拌均。二、花生油下锅烧至六成（有少量微油烟为准） ，将拌均的香椿芽倒入锅内，翻炒（期间检查油量，以花生油刚好浸到香椿芽高度表面为准）两分钟。三、盖上锅盖，用微火焖5分钟，关火，继续用余热焖制，待油焖香椿芽在有盖的锅内自然凉后，即可食之。 “油焖香椿”保持了香椿芽独特的香味，于独特的香味中闻辣、于闻辣中品香，且口感爽滑，是开胃、可口的绿色传统食品。</span></p><br /></p>', 'images/201204/thumb_img/1506_thumb_P_1334338441842.jpg', 'images/201204/goods_img/1506_P_1334338441200.jpg', 'images/201204/source_img/1506_P_1334338442920.jpg', '1', '', '1', '1', '0', '0', '1334338441', '100', '0', '0', '0', '0', '0', '0', '1334338441', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1507', '9', '36501001509', '怀参阿胶桂圆膏', '+', '0', '83', '', '1000', '0.000', '477.60', '398.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">　怀参阿胶桂圆膏是以怀参（怀山药）、桂圆、酸枣仁、大枣、枸杞、黑芝麻、阿胶等十六种药食两用材料，通过传统工艺洗、切、浸、泡、焙、煎、提、熬、收九道工序进行精华提取，然后煎熬浓缩而成的一种天然滋补养生品。怀参 —— 是指怀山药，铁棍山药。CCTV《健康之路》栏目，称之为“神仙之食”。《神农本草经》称薯蓣，一名山芋，药用其根，因其药效可与人参相比，又称“怀参”。  \n产地为中国焦作，焦作被称为“三百里怀川”，采撷了黄河上游各个地区不同地质条件的丰富营养，又吸纳了太行山岩溶地貌渗透下来的大量微量元素，土地疏松肥沃，排水快捷，雨量充沛，水质奇特，光照充足，气候温和。“春不过旱、夏不过热、秋不过涝、冬不过冷”的气候环境，最适宜山药、地黄、牛膝等蓄根类药材的生长。相传上古时代，炎帝神农氏身患重病，为医治，他带领文武百官，妻室家眷，跋山涉水，广走民间。在一个秋高气爽、晨气沁脾的季节，神农氏一行来至怀川时，当看到绿叶如盖、花团锦簇的美好景色和秀丽奇绝的灵山（今之神农山）风光时，大发感叹：“真乃神仙福地，药山矣！”遂在此辨五谷尝百草，登坛祭天，终得四样草根花蕊和水服之，不日痊愈。又令山、地、牛、菊四官护值，因人而得名“山药、地黄、牛膝、菊花”。这也就是后人所传“四大怀药”的最早起源。目前为国家地理保护原产地产品。\n编辑本段历史背景\n　　怀参阿胶桂圆膏，又称（怀府十全膏），它诞生于古老文明的中原大地，美丽的黄河岸边—古怀庆府。历史上的怀庆府包括现今焦作地区的武陟、修武、济源、沁阳、温县、博爱、原阳、孟州地区。得天独厚的自然条件让这里盛产世界闻名的四大怀药：怀山药、怀地黄、怀牛膝、怀菊花。 　　明清时期，怀庆府是北半个中国五大药材集散地之一。曾经的十三药帮中，怀帮处于领袖地位，在很多地区曾流传“十三帮一大片，比不上怀帮一个殿”。从清明开始，怀帮在全国建立了17个怀庆会馆，包括河南禹州、周口、北京、天津、晋城、武汉等地。 　　我国最早的药物学经典《神农本草经》，把“覃怀地”（怀川，怀庆府）所产的山药（薯蓣）、地黄、牛膝、菊花都列为上品。之后历代医药名家不断的去粗存精，进一步发现了四大怀药的优秀原始本性。张仲景的《伤寒论》、陶弘景的《名医别录》、孙思邈的《备急千金要方》、苏颂的《图经本草》、龚延贤的《寿世保元》、李时珍的《本草纲目》，以及《本经》、《药性类明》、《汤液本草》、《伤寒蕴要》、《得配本草》、《本经逢原》、《食疗本草》、《百一选方》以及叶天士、董建华、王永炎、姜良铎等近现代、当代中医名家,都对四大怀药做出了极为精到的评价,每每言及四大怀药的神奇效用与效力,言必褒誉有加。\n编辑本段怀参阿胶桂圆膏的介绍\n　　怀参阿胶桂圆膏（怀府十全膏）是由怀山药、阿胶、桂圆、大枣、黑芝麻、百合、枸杞、茯苓、莲子、酸枣仁等十几种名贵药食同源原料组成。所用原料均由道地产地提供，由经验丰富的专业人员进行检测与分拣，确保原料的纯正。怀参阿胶桂圆膏（怀府十全膏）是在我国滋膏古方的基础上，由专家精  \n心研究调制而成，并经过长达两年的效果确定。产品采用的均是既可入药又可作为食物的药食两用材料作为原料，产品没有添加香精、色素、糖、增稠剂、收膏剂、防腐剂等任何添加剂，，也没有添加任何如钙、磷、铁等微量元素。产品不是简单的将原料勾兑混合，完全是采用传统工艺通过洗、切、浸、泡、焙、煎、提、熬、收等九道工序煎熬浓缩成膏，将原料中的精华提取出来，浓缩成膏。产量极低，一百公斤原料仅出六公斤的成品膏。 　　膏者“神之液也”，膏体入口细腻、口感醇厚、后味悠长、易消化吸收。此膏体醇厚的口感和香气均是由各种原料本身所具有的特性阴阳相济而成，没有添加任何调剂口感的辅助香料。 　　中医理论上认为气血乃人之根本，所有的疾病均是由气血两亏引起。年轻人气血旺，所以有活力、精力好，不易生病就是这个道理。怀参阿胶桂圆膏（怀府十全膏）采用的均是药食同源的原料，产品充分提取浓缩十几种原料的特性，有补有润、有调有养、阴阳相融，通过食疗可以使人体气血旺、精气足，达到良好的身体状态。既避免了药物对人体的损害，又可以使食物发挥最大的功效。\n编辑本段怀参阿胶桂圆膏的成分\n　　【怀山药】 　　含有大量蛋白质，各种维生素和有益的微量元素，可以调节人体免疫系统，增强人体免疫力。健脾、补肺、固肾、益精，被称为“食物药”。以河南武陟温县（古怀庆府）所产质量最佳。 　　【阿胶】 　　滋阴补血，含有各种蛋白质、氨基酸、钙等，能改善血钙平衡，促进红细胞的生成。 　　【桂圆】 　　滋补强体、补心安神、养血壮阳、益脾开胃、润肤美容。含有大量对人体有益的微量元素、蛋白质、钙、磷、铁、抗坏血酸等。能够安神，治失眠、贫弱体衰、贫血、久病体虚等。  \n【大枣】 　　含有大量的维生素C、核黄素、硫胺素、胡萝卜素、尼克酸等多种维生素，具有较强的补养作用，能够提高人体免疫功能，增强抗病能力，补血，安神，健脾。 　　【黑芝麻】 　　富含大量的脂肪、蛋白质和人体必需的氨基酸，含有维生素A、维生素E、卵磷脂、钙、铁、等营养成分。可以补肝肾、润五脏、益气力、长肌肉、填脑髓。可用于身体虚弱、贫血萎黄、津液不足的人群。 　　【百合】 　　主要含生物素、秋水碱、淀粉、蛋白质、氨基酸等多种生物碱和营养物质。有润肺、清心、调中之效，良好的营养滋补之功，可止咳、止血、开胃、安神。特别是对病后体弱、神经衰弱等症大有裨益。 　　【枸杞】 　　富含甜菜碱、胡萝卜素、玉蜀黍黄素、烟酸、维生素Bl 、B2 、C 、钙、磷、铁、亚油酸及多种氨基酸。可以滋补肝肾、益精明目，用于虚痨精亏、腰膝酸痛、眩晕耳鸣、内热消渴、血虚萎黄。 　　【茯苓】 　　性味甘淡平，入心、肺、脾经。具有渗湿利水、健脾和胃、宁心安神的功效。 　　【莲子】 　　含有β－固甾醇，生物碱及丰富的钙、磷、铁等矿物质和维生素。可补脾止泻、益肾固精、养心安神。 　　【酸枣仁】 　　富含脂肪油、蛋白质和多量维生素C，具有宁心安神、敛汗生津、镇静助眠的功效，治疗虚烦不眠、惊悸健忘、体虚多汗、津少口干等症。、\n编辑本段和传统固元膏对比\n　　传统固元膏的配方主要以补为主，材料简单，而非有补有润，对肠胃比较虚弱的人来说补的过强，不但达不到补的效果，而且易生火、生热，反而对身体有害。而且制作固元膏只是把几种原料混合在一起捣碎、搅拌混合加工而已，制作程序简单，工艺粗糙，肠胃不易吸收，还达不到最佳的补、润、调、养效果。怀参阿胶桂圆膏则完全采用独有保密精制工艺，所有原料要经过洗、切、浸、泡、焙、煎、提、熬、收九道工序将原料中的精华提取出来，然后浓缩成膏。是在保证各种原材料的基本特性不受任何破坏的情况秘制而成，成品膏入口细腻柔软，味道自然甜美，回味悠长。再加上原料中的特殊保密配方，使产品从而真正实现了有补有润、有调有养、阴阳相融的作用，通过食疗的方法，可以使人的身体气血旺、精气足，达到良好的平和状态。\n编辑本段怀参阿胶桂圆膏的优势\n　　“药食同源”的生产材料 　　怀参阿胶桂圆膏是采用怀山药、阿胶、大枣、黑芝麻、枸杞、桂圆等十六种药食同源原材料，经九道工序熬制成的一种养生滋补食品。据专家介绍，采用“药食同源”既避免了药物对人的损害，又能使食物发挥最大的效能。所谓“药借食助、食助药威，寓药于食”吃出健康，吃出美丽，妙就妙在这里。 　　“纯天然，零添加” 　　怀参阿胶桂圆膏的另外一个特点就是产品不含任何防腐剂、添加剂。是纯天然的食疗养生极品。原料先是经过洗切，然后再按照食材本身的特性进行炮制，有效成分全部用水提取，最后浓缩成膏。整个制作过程中唯一加入的就是“水”，最大程度保留了原料的有效成分，避免了添加剂对人体的影响。为了保证产品的天然与纯正，怀参阿胶桂圆膏也没有人为地添加如钙、磷、铁、维生素等微量元素，所有的营养元素统统来自原料本身，可以提供天然的人体所需的各种营养成份。\n怀参阿胶桂圆膏原料成分图册(9张)　　“高纯度，高浓缩” 　　怀参阿胶桂圆膏是通过洗、切、浸、泡、焙、煎、提、熬、收九道工序进行精华提取，然后将其煎熬浓缩而成。由于它的特殊制作工艺使其产量极低，纯度极高，一百公斤原材料只能产出六公斤的成品膏，由于浓缩了各种原材料的精华，这也使其与普通固元膏相比价格昂贵了许多。 　　“口感好，易吸收” 　　怀参阿胶桂圆膏属于纯天然的养生滋补品，产品按照中医秘制配方生产，避免了药物在人体的堆砌，其膏体入口细腻，口感纯正，回味悠长。中医记载“膏者，神之液也”，膏是最容易被人体吸收的载体。人们食用之后会在补气补血提高身体免疫力方面有明显的帮助。膏体易被人体充分吸收，在短期内可达到补益，让身体生机盎然，一般食用2盒即有显著改善。男性补气，女性补血，由于兼顾气血双补，因此，怀参阿胶桂圆膏对男性女性都适合，特别对亚健康人群十分有益。怀参阿胶桂圆膏对女性还能改善面色黄、干枯老化的情况，对于经常疲惫、乏力、记忆力差的老年人也有很明显的调节效果。除亚健康人群外，怀参阿胶桂圆膏对处于产后、术后人群也有很好的调养和恢复作用。</span></p><br /></p>', 'images/201204/thumb_img/1507_thumb_P_1334338444270.jpg', 'images/201204/goods_img/1507_P_1334338444660.jpg', 'images/201204/source_img/1507_P_1334338445162.jpg', '1', '', '1', '1', '0', '0', '1334338444', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1508', '9', '36501001510', '精制腊肉', '+', '0', '84', '', '1000', '0.000', '33.60', '28.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">湖南腊肉，历史悠久，中外驰名。制作全过程分备料，腌渍，熏制三步。 　　1.备料：取皮薄肥瘦适度的鲜肉或冻肉刮去表皮肉垢污，切成0.8～1公斤、厚4～5厘米的标准带肋骨的肉条。如制作无骨腊肉，还要切除骨头。加工带骨腊肉用食盐7公斤、精硝0.2公斤、花椒0.4公斤。加工无骨腊肉用食盐2.5公斤、精硝0.2公斤、白糖5公斤、白酒及酱油各3.7公斤、蒸馏水3～4公斤。辅料配制前，将食盐和硝压碎，花椒、茴香、桂皮等香料晒干碾细。 　　2.腌渍有三种方法：（1）干腌。切好的肉条与干腌料擦抹擦透，按肉面向下顺序放入缸内，最上一层皮面向上。剩余干腌料敷在上层肉条上，腌渍3天翻缸。（2）湿腌。将腌渍无骨腊肉放入配制腌渍液中腌15～18小时，中间翻缸2次。（3）混合腌。将肉条用干腌料擦好放入缸内，倒入经灭过菌的陈腌渍液淹没肉条，混合腌渍中食盐用量不超过6%。 　　3.熏制有骨腌肉，熏前必须漂洗和晾干。通常每百公斤肉胚需用木炭8～9公斤、木屑12～14公斤。将晾好的肉胚挂在熏房内，引燃木屑，关闭熏房门，使熏烟均匀散布，熏房内初温70℃，3～4小时后逐步降低到50～56℃，保持28小时左右为成品。刚刚成的腊肉，须经过3～4个月的保藏使其成熟。\n编辑本段制作要诀\n　　1. 猪肉宜选皮落肥瘦相连的后腿肉或五花三层肉； 　　2. 熏料上若加桔皮少许，腊肉香味更加浓郁； 　　3. 需准备松柏木屑54克、干果壳54克作熏料用。\n编辑本段其他做法\n原料\n　　猪肉...... 5000克 白酒....... 50克 　　盐....... 150克 松柏木屑..... 54克 　　花椒....... 25克 干果壳...... 54克 　　白糖....... 50克\n烹制方法\n　　1.先将猪肉皮上残存的毛用刀刮干净，切成.. 3厘米宽的长条，用竹签扎些小眼，以利于进味。 　　2.先把花椒炒热，再放入盐炒烫，倒出晾凉。 　　3.将猪肉用花椒、盐、白糖揉搓，放在陶器盆内或搪瓷盆内，皮向下，肉向上，最上一层皮向上，用重物压上。冬春季两天翻一次，脆约5天取出，秋季放在凉爽之处，每天倒翻一至两次，脆约.. 2天取出，用净布抹干水分，用麻绳穿在一端皮上，挂于通风高处，晾到半干，放人熏柜内，熏约两三天，中途移动一次，使烟全部熏上腊肉都呈金黄色时，取挂于通风之处即成。\n工艺关键\n　　1.猪肉宜选皮落肥瘦相连的后腿肉或五花三层肉。 　　2.熏料上若加桔皮少许，腊肉香味更加浓郁。\n风味特点\n　　腊味是湖南特产，凡家禽野畜及水产等均可脆制，选料认真；制作精细，品种多样，具有色彩红亮，烟熏咸香，肥而不腻，鲜美异常的独特风味，每年冬初季节就开始熏制，要吃到春节之后。烟熏的腊味菜，能杀虫防腐，只要保管得法，一年四季都能品尝。</span></p><br /></p>', 'images/201204/thumb_img/1508_thumb_P_1334338448293.jpg', 'images/201204/goods_img/1508_P_1334338448446.jpg', 'images/201204/source_img/1508_P_1334338448367.jpg', '1', '', '1', '1', '0', '0', '1334338448', '100', '0', '0', '0', '0', '0', '0', '1334338448', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1509', '9', '36501001511', '湘西黄牛肉', '+', '0', '84', '', '1000', '0.000', '18.00', '15.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">西黄牛主产于湖南省湘西北地区。性情温驯，耐粗饲、耐热，体型中等，发育匀称，前躯略高，肌肉发达，骨骼结实，肩峰高，头短小，额宽阔，角形不一，颈细长，颈垂大，胸部发达，背腰平直，腰臀肌肉发达，尾长而细，四肢筋腱明显、强壮有力。毛色一般以黄色、褐色为主。成年公牛平均体重为334.3公斤，母牛为240.2公斤，屠宰率为39-54.4%，净肉率为46.87%。\n\n目录\n\n简介\n产地及环境\n地理分布\n品种特征\n品种性能\n 编辑本段简介\n  湘西黄牛\n　　主产于湖南省湘西北地区。性情温驯，耐粗饲、耐热，体型中等，发育匀称，前躯略高，肌肉发达，骨骼结实，肩峰高，头短小，额宽阔，角形不一，颈细长，颈垂大，胸部发达，背腰平直，腰臀肌肉发达，尾长而细，四肢筋腱明显、强壮有力。毛色一般以黄色、褐色为主。成年公牛平均体重为334.3公斤，母牛为240.2公斤，屠宰率为39-54.4%，净肉率为46.87%。\n编辑本段产地及环境\n　　湘西黄牛产于湖南省湘西土家族苗族自治州和慈利县，以及石门、桃源、沅陵、辰溪、麻阳、芷江、新晃等县的部分地方。主要产区为凤凰、花垣、大庸、桑植、永顺、慈利六县。产区地处云贵高原东北边缘，西南与黔东接壤，西北与川鄂毗邻，东南以雪峰山西部山脉为屏障，东北至澧水下游。位于东经109°14′～111°36′，北纬27°32′～29°44′。境内为武陵山脉所盘亘。山势陡峻，岭间为小块盆地与丘陵相间。海拔最高为1890米，最低为82米。澧水、沅水北部支流贯穿全境，向东北流入洞庭湖。气候温和，四季分明，属亚热带季风气候。年平均气温为16.1～16.8℃，无霜期240～270天，年降雨量为1300～1800毫米。但因境内地势复杂，而形成多种小气候。产区中山为黄壤，肥力较好，低山为黄棕壤，丘陵盆地多为红壤与紫色土。在这种生态条件下，亚热带植物发育甚好，森林覆盖率为34%。草场资源丰富，全区草山面积约1400万亩，其中山地草丛类，疏林草丛类和山地草甸类草场以禾本科草类为主，占59.6%。牧草主要有冬茅、野古草、马唐、雀稗、细柄草等。豆科牧草占21.7%，其余为菊科、蓼科等多种草类。产区山多坪少，地广人稀，耕地面积共410万亩，其中旱土占30.9%。梯田坡地多，生产条件差，耕作粗放。主要农作物有水稻、玉米，其次为小麦、红薯、大豆、马铃薯、荞麦、高梁，以及花生、芝麻、烟叶、甘蔗等。粮食产量中，水稻占60%，杂粮占40%。\n编辑本段地理分布\n　　湘西黄牛除湖南省湘南地区外，全省均有分布，并远销湖北、江西、河南，安徽、山东等省。\n编辑本段品种特征\n　　湘西黄牛体格中等，各部发育均称，体躯较短，前高后低，肌肉发达，骨骼坚实，皮肤富有弹性。湘西黄牛的头大小适中，头顶稍圆，公牛头短额宽，母牛头较秀长。眼大右神，眼眶稍突出，有少数的牛上眼脸和嘴四周有黄白色毛，俗称“粉嘴画眉”。耳薄，鼻镜宽，鼻孔大，嘴岔深。角形以“龙门”，倒八字形为多。公牛颈粗短而雄伟。湘西黄牛的颈部垂皮较发达。前胸开阔，鬐甲丰圆。公牛肩峰明显，背腰平直短宽，腹大而不下垂。母牛乳房不发达，公牛睾丸显露，大小匀称。四肢端正，蹄质坚实，蹄以黑色居多。尾较长，尾根较粗且着生部位高，帚毛密而多，越过飞节。全身毛色以黄色者最多，占60%以上，栗色、黑色次之，杂色很少。一般体躯上部毛色深，腹胁及四肢内侧毛色较浅。\n编辑本段品种性能\n　　湘西黄牛成年公牛体高平均为117.11厘米，体重平均为334.29公斤，成年母牛体高平均为106.06厘米，体重平均为240.24公斤。湘西黄牛役力较强，突击力和持久性能好，善于登山爬坡，步态稳健，行动灵敏。湘西黄牛的肉质优良、脂肪少，试销港澳市场，曾获好评。</span></p><br /></p>', 'images/201204/thumb_img/1509_thumb_P_1334338452162.jpg', 'images/201204/goods_img/1509_P_1334338452053.jpg', 'images/201204/source_img/1509_P_1334338452226.jpg', '1', '', '1', '1', '0', '0', '1334338451', '100', '0', '0', '0', '0', '0', '0', '1334338451', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1510', '9', '36501001514', '怀山药', '+', '0', '85', '', '1000', '0.000', '81.60', '68.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">铁棍山药，既是食用的佳蔬，又是人所共知的滋补佳品。它含有蛋白质、糖类、维生素、脂肪、胆碱、淀粉酶等成分，还含有碘、钙、铁、磷等人体不可缺少的无机盐和微量元素。中医认为，铁棍山药具有补脾养胃、补肺益肾的功效，可用于治疗脾虚久泻、慢性肠炎、肺虚咳喘、慢性胃炎、糖尿病、遗精、遗尿等症。\n\n1、调养脾胃。随着天气渐冷，有些脾胃虚弱的人，常常出现食少腹胀、少气懒言、大便稀溏、肢体倦怠等症状。薏米（薏苡仁）10克，铁棍山药10克（鲜铁棍山药 50克），大米100克，水适量煮成粥；或铁棍山药500克，河鱼一条（约250克，去杂洗净），加适量的盐、酒、姜、葱等调料一起煮汤。适用于脾虚所致的腹胀、泄泻。\n\n2、补益强身。平时体虚之人，应趁秋凉时加紧进补，以备冬天不时之需。铁棍山药补而不热，温而不燥，最适合老年人需要，常吃有益无害，每天吃铁棍山药100克左右，连续吃1~2个月。\n\n3、补肺润燥。久病虚损的慢性咳嗽，可以吃铁棍山药调洽。铁棍山药是天然补肺润燥之品，在临床上是治疗肺虚久咳、虚喘的济世良药，每天250克左右，缓以图功。\n\n4、涩精止尿。遗精、尿频都是肾虚所致，铁棍山药补脾的同时，还能补肾，提前吃点铁棍山药，可以防患于未然，对冬季易加重的上述疾病大有好处。吃法简单，不拘量，常服之。 5、润肤祛燥。铁棍山药能生津润燥，有滋养皮肤、毛发的功能，故有美容作用。秋冬季皮肤极易干燥，使人毛发枯槁，容颜失华，因此多吃铁棍山药，能润泽皮肤和毛发。可以用铁棍山药500克煮粥，早晚常喝。\n\n\n \n\n</span></p><br /></p>', 'images/201204/thumb_img/1510_thumb_P_1334338455037.jpg', 'images/201204/goods_img/1510_P_1334338455277.jpg', 'images/201204/source_img/1510_P_1334338456887.jpg', '1', '', '1', '1', '0', '0', '1334338455', '100', '0', '0', '0', '0', '0', '0', '1334338455', '1', '', '-1', '-1', '', '', '');
