INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1513', '9', '36501001520', '新竹米粉', '+', '0', '0', '', '1000', '0.000', '20.40', '17.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">重量(g): 250\n \n\n\n\n\n\n台湾美馔新竹米粉\n\n新竹米粉是台灣新竹地區的一種知名食材。以米作為原料的米粉，原本是由大陸福建地區傳入，由於新竹地區經年強風，獨特的氣候環境非常適合製造米粉，因而逐漸發展成地方性的特產。台湾新竹县风味独特的新竹米粉，米粉在台湾米食中扮演着重要角色。台湾新竹县的客雅溪一带水质甘美，风势很大，而且湿度适宜，风干的米粉別具风味，再加上独特的加工秘方，新竹米粉闻名遐迩。客雅溪畔米粉厂林立，米粉加工成为当地群众创收的一大支柱新竹米粉是台灣新竹地區的一種知名食材。以米作為原料的米粉，原本是由大陸福建地區傳入，由於新竹地區經年強風，獨特的氣候環境非常適合製造米粉，因而逐漸發展成地方性的特產。 在分類上，新竹米粉又可區分為「水粉」和「炊粉」兩種。其中被稱為水粉、形狀粗短的粗米粉才是米粉的原型，最常見到的作法是與肉類熬煮成米粉湯。而今日比較廣為人知的是稱為炊粉的細米粉，是先壓製成細絲之後以蒸籠蒸熟，再經過風乾以便利保存。在台灣常見的炊粉食用方式，除了煮成類似湯麵般的料理（例如貢丸米粉湯），先將米粉炒熟之後再澆淋醬油肉汁食用的米粉炒也是常見的台式菜色。。\n</span></p><br /></p>', 'images/201204/thumb_img/1513_thumb_P_1334338467213.jpg', 'images/201204/goods_img/1513_P_1334338467373.jpg', 'images/201204/source_img/1513_P_1334338467922.jpg', '1', '', '1', '1', '0', '0', '1334338466', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1514', '9', '36501001521', '柴葛茶', '+', '0', '75', '', '1000', '0.000', '19.20', '16.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">葛根是豆科植物葛的块根，属多年生植物，其花为淡紫色，呈不规则的扁长圆形或略成扁肾形，野生葛生长于山坡草丛，灌丛林缘、路旁、溪边及较阴湿地带，易于生长，在我国广泛分布于重庆、贵州、湖南张家界、河北、甘肃等地。本品是以生长在高山丛林中的野生葛根为原料，经清洗、切片、干燥等工序加工而成,本成品属原汁原味的纯天然食品。本品富含葛根素、生物碱、蛋白质、铁、锌、钙等微量元素和Vb、Vc等多种维生素。\n</span></p><br /></p>', 'images/201204/thumb_img/1514_thumb_P_1334338470343.jpg', 'images/201204/goods_img/1514_P_1334338470651.jpg', 'images/201204/source_img/1514_P_1334338470104.jpg', '1', '', '1', '1', '0', '0', '1334338469', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1515', '9', '36501001522', '青藏大黄', '+', '1', '22', '', '1000', '0.000', '69.60', '58.00', '0.00', '0', '0', '1', '', '', '<p>\n	<span style=\"line-height:2.5;font-size:14px;\">可可西里大黄（260g）： 大黄是我国的四大中药之一。青海大黄是产于青藏高原海拔3000米以上的野生珍品，是大黄中的优良品种，又名“排毒将军”，青藏高原独特的地理环境、生态环境使本品具有野生、纯净、无污染的特性，是青藏高原特有的药用植物，藏医学药典和中国药典均有详细记载。长期饮用，可排毒养颜，润肠通便、保健效果显著 。【功效与作用】现代科学研究表明：大黄含有大黄酸，芦荟大黄素，番泻甙等多种生物活性物质和微量元素，可清热祛火,消脂减肥，保肝利胆，润肠通便，改善肾功能，清洁内环境，排毒养颜，延缓衰老。【宜忌】妇女怀孕、月经期、哺乳期应忌用。【食用方法】泡酒、入汤剂、用开水泡服 </span>\n</p>\n<p>\n	<br />\n</p>', 'images/201204/thumb_img/1515_thumb_P_1334338474227.jpg', 'images/201204/goods_img/1515_P_1334338474412.jpg', 'images/201204/source_img/1515_P_1334338474186.jpg', '1', '', '1', '1', '0', '0', '1334338473', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1516', '9', '36501001523', '中宁枸杞', '+', '0', '0', '', '1000', '0.000', '44.40', '37.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">宁夏是枸杞原产地，栽培枸杞已有500多年的历史，而中宁枸杞则是宁夏枸杞中之上品。中宁枸杞之所以名甲天下，其一功益于当地适于枸杞生长的土壤和昼夜温差大的气候；其二是利用黄河水与含有各种矿物质的清水河苦水混灌。其特定条件决定了中宁枸杞的与众不同，中宁枸杞色艳、粒大、皮薄、肉厚、籽少、甘甜，品质超群，是惟一被载入新中国药典的枸杞品种，国家医药管理局将宁夏定为全国惟一的药用枸杞产地，引入全国十大药材生产基地之一。明代杰出医药学家李时珍所著《本草纲目》中，将宁夏枸杞列为本经上品，称\"全国入药杞子，皆宁产也\"。  \n长期以来，中宁枸杞子一直以品质纯正、产量丰盈而居全国之冠。据史籍载，中宁栽培枸杞至少已有五百年的历史，明弘治年间即被例为“贡果”。编纂于清乾隆是的《中卫县志》称：“枸杞宁安一带（今宁夏中宁县）家种杞园，各省入药甘枸杞皆宁产也”。时人曾赋诗赞曰：“六月杞园树树红，宁安药果擅寰中。千钱一斗矜时价，决胜腴田岁早丰。”《朔方道志》中也有“枸杞宁安堡者佳”的记载。唐朝著名诗人刘禹锡曾赞誉枸杞“枝繁本是仙人杖，根老能成瑞犬形。上品功能甘露味，还知一勺可延龄。” 　　中宁枸杞通常每年开花两次，夏采者称“夏果”，秋采者谓“秋果”。鲜枸杞子色泽艳红，似纺锤形，壮如枣核。经传统工艺加工后，干枸杞子，久贮不腐。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334338477', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1517', '9', '36501001524', '甘草', '+', '0', '61', '', '1000', '0.000', '22.56', '18.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">甘草是一种补益中草药。药用部位是根及根茎，药材性状根呈圆柱形，长25～lOOcm，直径0.6～3.5cm。外皮松紧不一，表面红棕色或灰棕色。根茎呈圆柱形，表面有芽痕，断面中部有髓。气微，味甜而特殊。功能主治清热解毒，祛痰止咳、脘腹等。喜阳光充沛，日照长气温低的干燥气候。甘草多生长在干旱、半干旱的荒漠草原、沙漠边缘和黄土丘陵地带。\n</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食疗效果</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">甘草是临床最常应用的药品。生甘草能清热解毒，润肺止咳，调和诸药性；炙甘草能补脾益气，临床用量特大，出口量大。除药用之外，食品上也大量用甘草做糕点添加剂，它的甜度是蔗糖的百倍。西方国家大量进口甘草，从中提取甘草次酸，治艾滋病。甘草退耕还林，水土保持作用更强。陕北农民和西部人们反映，把甘草种子随便撤到地上即可出苗，抗旱性特强。</span></p><p><br /></p>', 'images/201204/thumb_img/1517_thumb_P_1334338478654.jpg', 'images/201204/goods_img/1517_P_1334338478762.jpg', 'images/201204/source_img/1517_P_1334338478286.jpg', '1', '', '1', '1', '0', '0', '1334338477', '100', '0', '0', '0', '0', '0', '0', '1334339123', '1', '', '-1', '-1', '', '', '');
