INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1521', '19', '365010015216', '龙王女人豆浆粉', '+', '0', '98', '', '1000', '0.000', '18.00', '15.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">【配料】东北有机黄豆 麦芽糖浆 卵磷脂 乳酸钙 乳酸亚铁 乳酸锌 \n【净含量】540g\n【保质期】12个月\n本品原料出自于黑龙江省海伦地区的龙王非转基因大豆生产基地，这里是目前世界上仅有的三大黑土地之一，并著有“中国大豆之乡”和“寒地黑土”之美誉。得天独厚的原料优势和严格的管理及独特的产品配方，同时采用了融合日本、丹麦等国际最先进的生产工艺，保留了大豆的天然精华。 优越的生态环境，领先的科技生产，成就了龙王豆浆粉安全、无污染、营养、健康的消费时尚，为千家万户带去无限的健康呵护。\n本产品不含蔗糖、纯天然、低热量、含丰富的优质植物蛋白，提供充分营养、含有女性所需多种维生素、矿物质、微量元素、富含对女性有特殊生理效用的天然活性成分--大豆卵磷脂、大豆异黄酮等。\n祖国医学认为豆浆性质平和，女人喝豆浆好处多，春秋饮豆浆，滋阴润燥，调和阴阳；夏饮豆浆，消热防暑，生津解渴；冬饮豆浆，祛寒暖胃，滋养进补。\n女人多贫血，豆浆对贫血病人的调养作用比牛奶要强。中老年女人喝豆浆，可调节内分泌、延缓衰老；青年女性喝豆浆，则美白养颜淡化暗疮。\n研究认为，女性衰老和雌激素减少有关，而豆浆中的大豆异黄酮、大豆蛋白、卵磷脂等，是公认的天然雌激素补充剂，可预防危害女性健康的病症。豆浆中含有的异黄酮和卵磷脂可以调节女性雌激素及女性荷尔蒙分泌。\n豆浆中还含有一种特殊的植物雌激素“黄豆苷原”，可调节女性内分泌，每天坚持喝豆浆的女性，可明显改善心态和身体素质，延缓衰老，美容养颜。\n\n</span></p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>食用方法</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">【食用方法】取本产品一小袋，将其徐徐倒入180-220毫升80度的热水中，即时搅拌均匀溶解即可饮用。夏季冷藏口感更佳。</span></p><p><br /></p><p><span style=\"font-size:24px;background-color:#E53333;\"><strong>保存注意事项</strong></span> </p><p><span style=\"line-height:2.5;font-size:14px;\">储存方法：阴凉干燥密封，常温贮存,开口后请密封，否则受潮后不易溶解</span></p><p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334338582', '100', '0', '0', '0', '0', '0', '0', '1334338582', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1522', '17', '365010015229', '薰衣草蜜', '+', '0', '0', '', '1000', '0.000', '69.60', '58.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">    薰衣草蜜是从每年新疆素有“中国薰衣草之乡”美誉的伊犁薰衣草竟相绽放的5-6月间，蜜蜂从其花蕊中采集蜜汁与自身分泌物(主要是生物复合酶)结合后在蜂巢中转化、脱水、酿造而成的一级成熟蜂蜜，没有添加任何其它成分。\n    蜜色泽为琥珀色，有浓郁悠长薰衣草花香气味，口感清新爽口，易结晶。蜂蜜中薰衣草蜂蜜：天然纯正，无任何污染。含有丰富的矿物质、有机酸、蛋白质、维生素等各种营养物质，具有优异的抗菌性、抗炎性、抗氧化的自然愈合力。\n    蜂蜜可用于体弱多病、病后恢复的辅助治疗。患有高血压的老人，每天早晚各饮1杯蜂蜜水是非常有益的，因为蜜中的钾进入人体后有排钠的作用，可改善血中电解质平衡；对于肺结核、胃肠道溃疡等慢性病患者，蜂蜜是良好的营养品。蜂蜜还是一种天然的美容佳品。\n\n</span></p><br /></p>', 'images/201204/thumb_img/1522_thumb_P_1334338582436.jpg', 'images/201204/goods_img/1522_P_1334338582231.jpg', 'images/201204/source_img/1522_P_1334338583476.jpg', '1', '', '1', '1', '0', '0', '1334338582', '100', '0', '0', '0', '0', '0', '0', '1334338582', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1523', '17', '365010015236', '天山黑蜂蜂蜜', '+', '0', '87', '', '1000', '0.000', '45.60', '38.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">新疆黑蜂是新疆独特的优良品种，世界四大名蜂之一，中国蜜蜂的特殊基因，300多年前从国外引进，经过漫长的人工淘汰和自然淘汰，已形成适合新疆地理气候的一个新物种，具有采集力强、采胶量大、耐低温、抗病性强等特点，是世界上惟一可以利用零星蜜源进行采蜜的蜂种。新疆黑蜂共有300群，其中品质纯正的只有100多群。新疆黑蜂体型大、体质好、体能强。抗病能力强于其它任何品种的蜜蜂。抗寒适应性强，在零下30度以下的寒冬里能安全越冬，在8摄氏度的气温中还能到野外采蜜。飞行高度大，采蜜半径大，可采集到海拔1800米-2500米天山深处无污染的鲜花分泌液。因此新疆黑蜂所产蜂蜜具有含糖量高，矿物质、有机酸、蛋白质、维生素和酶等营养成份丰富的特点。但新疆黑蜂性情凶暴，不易驯养，濒临灭绝，故其所产蜂蜜等产品尤为珍贵。热合曼蜂产品旗下蜂场均使用新疆黑蜂，采集花粉酿造蜂蜜，确保产品的优良品质。\n\n    \n\n  黑蜂蜂蜜主要有以下几个特点：\n     一、绿色品质突出：天山地处偏远，交通落后，未受到近代工业、生活污染，是全国农牧业有机食品基地之一，在蜂蜜标准中，天山黑蜂蜂蜜重金属含量指标远远低于国家、国际指标，绿色品质突出。\n     二、多样性的中药保健成份：天山的地貌特点，造就了其植物的多样性，药用植物比重大，蜜源植物中，所涉及的100多种山花植物中，较为典型的有党参、益母草、贝母、野薄荷、百里香、甘草等，其保健营养、调理功能可见一斑。\n     三、活性酶成份含量高，口感浓烈独特。新疆黑蜂蜂蜜含有多种维生素和芳香物质，气味芳香袭人，口感甜润绵长。黑蜂山花蜜的淀粉酶（衡量蜂蜜活性最重要的指标）可达10以上，而国家标准为4，国际标准为8，这样的指标当然得益于新疆天山珍稀的生态资源，充分显示出黑蜂蜂蜜的野生蜜源价值\n</span></p><br /></p>', 'images/201204/thumb_img/1523_thumb_P_1334338585260.jpg', 'images/201204/goods_img/1523_P_1334338586228.jpg', 'images/201204/source_img/1523_P_1334338586327.jpg', '1', '', '1', '1', '0', '0', '1334338585', '100', '0', '0', '0', '0', '0', '0', '1334338585', '1', '', '-1', '-1', '', '', '');
